import React, { useState, useEffect, useCallback } from "react";
import styled from "styled-components";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";
import { 
  Elements, 
  CardNumberElement, 
  CardExpiryElement, 
  CardCvcElement, 
  useStripe, 
  useElements 
} from "@stripe/react-stripe-js";

const stripePromise = loadStripe("pk_test_51SEWfbGUJ7v3D9GXVs5sPIBEY08wZrpGmeuko8YClRovgbeQkkYJ2NpxLMtM3RyA8v7NSKM4cE8mTLthWafiYGv500lPgVJb3n");

// ========== ALL STYLED COMPONENTS ==========
const Container = styled.div``;

const Wrapper = styled.div`
  padding: 20px;
`;

const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`;

const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
`;

const TopButton = styled.button`
  padding: 10px;
  font-weight: 600;
  cursor: pointer;
  border: ${(props) => props.type === "filled" && "none"};
  background-color: ${(props) =>
    props.type === "filled" ? "black" : "transparent"};
  color: ${(props) => props.type === "filled" && "white"};
`;

const TopTexts = styled.div``;

const TopText = styled.span`
  text-decoration: underline;
  cursor: pointer;
  margin: 0px 10px;
`;

const Bottom = styled.div`
  display: flex;
  justify-content: space-between;
`;

const Info = styled.div`
  flex: 3;
`;

const Product = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
`;

const ProductDetail = styled.div`
  flex: 2;
  display: flex;
`;

const Image = styled.img`
  width: 200px;
  height: 200px;
  object-fit: cover;
`;

const Details = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
`;

const ProductName = styled.span`
  font-size: 18px;
  font-weight: 500;
`;

const ProductId = styled.span`
  color: gray;
`;

const PriceDetail = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const ProductAmountContainer = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
`;

const ProductAmount = styled.div`
  font-size: 24px;
  margin: 5px 15px;
`;

const ProductPrice = styled.div`
  font-size: 30px;
  font-weight: 200;
`;

const Hr = styled.hr`
  background-color: #eee;
  border: none;
  height: 1px;
  margin: 20px 0;
`;

const Summary = styled.div`
  flex: 1;
  border: 0.5px solid lightgray;
  border-radius: 10px;
  padding: 20px;
  height: fit-content;
`;

const SummaryTitle = styled.h1`
  font-weight: 200;
`;

const SummaryItem = styled.div`
  margin: 30px 0px;
  display: flex;
  justify-content: space-between;
  font-weight: ${(props) => props.type === "total" && "500"};
  font-size: ${(props) => props.type === "total" && "24px"};
`;

const SummaryItemText = styled.span``;

const SummaryItemPrice = styled.span``;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  background-color: black;
  color: white;
  font-weight: 600;
  border: none;
  cursor: pointer;
  margin-top: 10px;
  
  &:disabled {
    background-color: #ccc;
    cursor: not-allowed;
  }
`;

const QuantityButton = styled.button`
  width: 30px;
  height: 30px;
  border: 1px solid teal;
  background-color: white;
  cursor: pointer;
  font-size: 18px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const EmptyCart = styled.div`
  text-align: center;
  padding: 50px;
  font-size: 18px;
  color: gray;
`;

const FreeShippingBadge = styled.span`
  color: green;
  margin-left: 5px;
  font-size: 12px;
  font-weight: 600;
`;

const AddressForm = styled.div`
  margin: 20px 0;
  padding: 20px;
  border: 1px solid #eee;
  border-radius: 10px;
  background-color: #f9f9f9;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin: 8px 0;
  border: 1px solid #ddd;
  border-radius: 4px;
  font-size: 14px;
`;

const InputRow = styled.div`
  display: flex;
  gap: 10px;
  
  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const InputGroup = styled.div`
  flex: 1;
`;

const PaymentModal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const PaymentContent = styled.div`
  background: white;
  padding: 2rem;
  border-radius: 12px;
  width: 90%;
  max-width: 500px;
  text-align: center;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
`;

const PayNowBtn = styled.button`
  background: #4CAF50;
  color: white;
  padding: 12px 24px;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin: 5px;
  font-size: 16px;
  font-weight: 600;
  width: 100%;
  transition: background 0.3s;

  &:hover {
    background: #45a049;
  }

  &:disabled {
    background: #cccccc;
    cursor: not-allowed;
  }
`;

const CancelBtn = styled.button`
  background: #f44336;
  color: white;
  padding: 12px 24px;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin: 5px;
  font-size: 16px;
  font-weight: 600;
  width: 100%;
  transition: background 0.3s;

  &:hover {
    background: #da190b;
  }
`;

const ErrorMessage = styled.div`
  color: #d32f2f;
  background-color: #ffebee;
  padding: 12px;
  border-radius: 6px;
  margin: 15px 0;
  font-size: 14px;
  border: 1px solid #f5c6cb;
`;

const SuccessMessage = styled.div`
  color: #155724;
  background-color: #d4edda;
  padding: 12px;
  border-radius: 6px;
  margin: 15px 0;
  font-size: 14px;
  border: 1px solid #c3e6cb;
`;

const CardFormContainer = styled.div`
  margin: 20px 0;
  text-align: left;
`;

const CardFieldContainer = styled.div`
  margin-bottom: 15px;
`;

const CardFieldLabel = styled.label`
  display: block;
  margin-bottom: 5px;
  font-weight: 600;
  color: #333;
  font-size: 14px;
`;

const CardFieldWrapper = styled.div`
  padding: 12px;
  border: 2px solid #e1e5e9;
  border-radius: 8px;
  background: white;
  transition: border-color 0.3s;
  
  &:focus-within {
    border-color: #4CAF50;
    box-shadow: 0 0 0 2px rgba(76, 175, 80, 0.1);
  }
`;

const CardRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 15px;
  
  @media (max-width: 480px) {
    grid-template-columns: 1fr;
  }
`;

const LoadingSpinner = styled.div`
  text-align: center;
  padding: 20px;
  color: #666;
`;

// ========== STRIPE PAYMENT FORM COMPONENT ==========
const StripePaymentForm = ({ amount, clientSecret, onSuccess, onError }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    if (!stripe || !elements) {
      return;
    }

    setProcessing(true);
    setError('');

    try {
      const cardNumber = elements.getElement(CardNumberElement);

      const { error: stripeError, paymentMethod } = await stripe.createPaymentMethod({
        type: 'card',
        card: cardNumber,
      });

      if (stripeError) {
        setError(stripeError.message);
        onError(stripeError.message);
        return;
      }

      console.log('üîÑ Confirming card payment...');
      
      const { error: confirmError, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: paymentMethod.id,
      });

      if (confirmError) {
        setError(confirmError.message);
        onError(confirmError.message);
      } else if (paymentIntent.status === 'succeeded') {
        console.log('‚úÖ Payment successful!');
        onSuccess(paymentIntent);
      } else {
        setError(`Payment status: ${paymentIntent.status}`);
        onError(`Payment status: ${paymentIntent.status}`);
      }
    } catch (err) {
      console.error('‚ùå Payment error:', err);
      setError('Payment failed. Please try again.');
      onError(err.message);
    } finally {
      setProcessing(false);
    }
  };

  const cardElementOptions = {
    style: {
      base: {
        fontSize: '16px',
        color: '#424770',
        fontFamily: '"Helvetica Neue", Helvetica, sans-serif',
        '::placeholder': {
          color: '#aab7c4',
        },
        backgroundColor: 'transparent',
      },
      invalid: {
        color: '#9e2146',
        iconColor: '#9e2146',
      },
    },
  };

  return (
    <CardFormContainer>
      <h3 style={{ marginBottom: '15px', color: '#333' }}>Payment Details</h3>
      <p style={{ fontSize: '18px', fontWeight: '600', color: '#2c5aa0', marginBottom: '20px' }}>
        Total Amount: <span style={{ color: '#4CAF50' }}>${amount}</span>
      </p>
      
      <form onSubmit={handleSubmit}>
        <CardFieldContainer>
          <CardFieldLabel>Card Number</CardFieldLabel>
          <CardFieldWrapper>
            <CardNumberElement
              options={{
                ...cardElementOptions,
                placeholder: '1234 1234 1234 1234',
              }}
            />
          </CardFieldWrapper>
        </CardFieldContainer>

        <CardRow>
          <CardFieldContainer>
            <CardFieldLabel>Expiry Date</CardFieldLabel>
            <CardFieldWrapper>
              <CardExpiryElement
                options={{
                  ...cardElementOptions,
                  placeholder: 'MM/YY',
                }}
              />
            </CardFieldWrapper>
          </CardFieldContainer>

          <CardFieldContainer>
            <CardFieldLabel>CVC</CardFieldLabel>
            <CardFieldWrapper>
              <CardCvcElement
                options={{
                  ...cardElementOptions,
                  placeholder: '123',
                }}
              />
            </CardFieldWrapper>
          </CardFieldContainer>
        </CardRow>
        
        {error && (
          <ErrorMessage style={{ textAlign: 'left' }}>
            ‚ö†Ô∏è {error}
          </ErrorMessage>
        )}
        
        <PayNowBtn type="submit" disabled={!stripe || processing}>
          {processing ? (
            <>
              <span style={{ marginRight: '8px' }}>‚è≥</span>
              Processing Payment...
            </>
          ) : (
            `Pay $${amount}`
          )}
        </PayNowBtn>
      </form>

      <div style={{ 
        marginTop: '20px', 
        padding: '15px', 
        background: '#f8f9fa', 
        borderRadius: '8px', 
        fontSize: '13px',
        textAlign: 'left',
        border: '1px solid #e9ecef'
      }}>
        <h4 style={{ margin: '0 0 10px 0', color: '#495057', fontSize: '14px' }}>
          üß™ Test Card Information
        </h4>
        <div style={{ lineHeight: '1.6', color: '#6c757d' }}>
          <div><strong>Card Number:</strong> 4242 4242 4242 4242</div>
          <div><strong>Expiry Date:</strong> 12/34</div>
          <div><strong>CVC:</strong> 123</div>
          <div><strong>ZIP Code:</strong> 12345</div>
        </div>
      </div>
    </CardFormContainer>
  );
};

// ========== MAIN CART PAGE COMPONENT ==========
const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isCheckingOut, setIsCheckingOut] = useState(false);
  const [showAddressForm, setShowAddressForm] = useState(false);
  const [showPayment, setShowPayment] = useState(false);
  const [paymentError, setPaymentError] = useState('');
  const [paymentSuccess, setPaymentSuccess] = useState('');
  const [clientSecret, setClientSecret] = useState('');
  const [currentOrderId, setCurrentOrderId] = useState('');
  const navigate = useNavigate();

  const [address, setAddress] = useState({
    street: "",
    city: "",
    state: "",
    zipCode: "",
    country: "United States"
  });

  // Helper Functions
  const getAuthHeader = () => {
    const token = localStorage.getItem('token');
    return token ? { Authorization: `Bearer ${token}` } : {};
  };

  const getCurrentUser = () => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const base64Payload = token.split('.')[1];
        const payload = JSON.parse(atob(base64Payload));
        return payload.userId;
      } catch (error) {
        return null;
      }
    }
    return null;
  };

  const calculateShipping = () => {
    const subtotal = getTotal();
    if (subtotal >= 50) {
      return { shipping: 0, hasFreeShipping: true };
    } else {
      return { shipping: 5.90, hasFreeShipping: false };
    }
  };

  const getTotal = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price) * item.quantity, 0);
  };

  const getFinalTotal = () => {
    const shippingInfo = calculateShipping();
    return getTotal() + shippingInfo.shipping;
  };

  const fetchCart = useCallback(async () => {
    const userId = getCurrentUser();
    if (!userId) {
      setCartItems([]);
      return;
    }

    try {
      setLoading(true);
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        { headers: getAuthHeader() }
      );
      setCartItems(response.data);
    } catch (error) {
      console.error('Error fetching cart:', error);
    } finally {
      setLoading(false);
    }
  }, []);

  const updateCartItem = async (productId, quantity) => {
    const userId = getCurrentUser();
    if (!userId) return;

    try {
      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        { productId, quantity },
        { headers: getAuthHeader() }
      );
      await fetchCart();
    } catch (error) {
      console.error('Error updating cart:', error);
    }
  };

  const removeFromCart = async (productId) => {
    const userId = getCurrentUser();
    if (!userId) return;

    try {
      await axios.delete(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}/${productId}`,
        { headers: getAuthHeader() }
      );
      await fetchCart();
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  };

  useEffect(() => {
    fetchCart();
  }, [fetchCart]);

  const handleQuantity = (productId, quantity) => {
    if (quantity === 0) {
      removeFromCart(productId);
    } else {
      updateCartItem(productId, quantity);
    }
  };

  const handlePayment = async () => {
    setPaymentError('');
    setPaymentSuccess('');
    setIsCheckingOut(true);

    try {
      const userId = getCurrentUser();
      const token = localStorage.getItem('token');
      const BASE_URL = process.env.REACT_APP_API_URL;

      // Validate address first
      if (!address.street || !address.city || !address.state || !address.zipCode || !address.country) {
        throw new Error('Please fill in all address fields');
      }

      // Validate cart items
      if (cartItems.length === 0) {
        throw new Error('Cart is empty');
      }

      console.log('üîÑ Starting payment process...');
      console.log('üì¶ Cart items:', cartItems);
      console.log('üè† Address:', address);
      console.log('üí∞ Total amount:', getFinalTotal());

      // Step 1: Create order FIRST with proper data structure
      const orderData = {
        cartItems: cartItems.map(item => ({
          productId: item.productId || item.id,
          title: item.title || item.name,
          price: parseFloat(item.price),
          quantity: item.quantity,
          size: item.size || 'One Size',
          color: item.color || 'Default',
          img: item.img || item.image
        })),
        address: address,
        totalAmount: getFinalTotal()
      };

      console.log('üì¶ Creating order with data:', orderData);

      const orderResponse = await axios.post(
        `${BASE_URL}/api/orders`,
        orderData,
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          timeout: 10000
        }
      );

      console.log('‚úÖ Order creation response:', orderResponse.data);

      if (!orderResponse.data.order || !orderResponse.data.order.id) {
        throw new Error('Order creation failed - no order ID returned');
      }

      const createdOrderId = orderResponse.data.order.id;
      setCurrentOrderId(createdOrderId);

      console.log('‚úÖ Order created successfully:', createdOrderId);

      // Step 2: Create payment intent with the order ID
      const amount = Math.round(getFinalTotal() * 100);

      console.log('üí≥ Creating payment intent for amount:', amount);

      const paymentResponse = await axios.post(
        `${BASE_URL}/api/payments/create-payment-intent`,
        { 
          orderId: createdOrderId,
          amount: amount,
          currency: 'usd'
        },
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          timeout: 10000
        }
      );

      console.log('‚úÖ Payment intent response:', paymentResponse.data);

      if (paymentResponse.data.clientSecret) {
        console.log('‚úÖ Payment intent created, client secret received');
        setClientSecret(paymentResponse.data.clientSecret);
      } else {
        throw new Error('No client secret received from payment service');
      }

    } catch (error) {
      console.error('‚ùå Payment setup error:', error);
      const errorMessage = error.response?.data?.error || error.response?.data?.message || error.message;
      setPaymentError(`Payment setup failed: ${errorMessage}`);
      setIsCheckingOut(false);
    }
  };

  const handlePaymentSuccess = async (paymentIntent) => {
    try {
      const userId = getCurrentUser();
      const token = localStorage.getItem('token');
      const BASE_URL = process.env.REACT_APP_API_URL;
      const userEmail = JSON.parse(localStorage.getItem('user'))?.email;

      console.log('‚úÖ Payment successful, paymentIntent:', paymentIntent);
      console.log('üîÑ Confirming payment with backend...');

      // Step 1: Confirm payment with backend
      const paymentConfirmation = await axios.post(
        `${BASE_URL}/api/payments/confirm-payment`,
        { 
          paymentIntentId: paymentIntent.id,
          orderId: currentOrderId 
        },
        { 
          headers: { Authorization: `Bearer ${token}` },
          timeout: 10000
        }
      );

      console.log('‚úÖ Payment confirmed by backend:', paymentConfirmation.data);

      if (paymentConfirmation.data.success || paymentConfirmation.data.status === 'succeeded') {
        
        // Step 2: Update order payment status
        try {
          const orderUpdateResponse = await axios.put(
            `${BASE_URL}/api/orders/${currentOrderId}/payment-status`,
            { 
              paymentStatus: 'paid'
            },
            { 
              headers: { Authorization: `Bearer ${token}` },
              timeout: 5000
            }
          );
          console.log('‚úÖ Order payment status updated:', orderUpdateResponse.data);
        } catch (orderError) {
          console.error('‚ö†Ô∏è Order status update failed:', orderError);
          // Continue anyway - order exists but status might not be updated
        }

        // Step 3: Clear cart
        console.log('üõí Clearing cart for user:', userId);
        
        try {
          await axios.delete(
            `${BASE_URL}/api/cart/${userId}/clear`,
            { 
              headers: { Authorization: `Bearer ${token}` },
              timeout: 5000
            }
          );
          console.log('‚úÖ Cart cleared successfully');
        } catch (cartError) {
          console.error('‚ö†Ô∏è Cart clearing failed:', cartError);
          // Continue anyway
        }

        // Clear cart locally
        setCartItems([]);

        // Step 4: Prepare success data
        const orderDataForSuccess = {
          id: currentOrderId,
          amount: getFinalTotal().toFixed(2),
          products: cartItems.map(item => ({
            title: item.title || item.name,
            price: item.price,
            quantity: item.quantity,
            img: item.img || item.image,
            size: item.size,
            color: item.color
          })),
          address: address,
          paymentMethod: 'Credit Card',
          status: 'confirmed',
          paymentStatus: 'paid',
          createdAt: new Date().toISOString(),
          paymentIntentId: paymentIntent.id
        };

        localStorage.setItem('lastOrder', JSON.stringify(orderDataForSuccess));
        localStorage.setItem('userEmail', userEmail);

        console.log('üéâ Payment completed successfully, redirecting...');

        // Show success message briefly before redirect
        setPaymentSuccess('Payment successful! Redirecting to order confirmation...');
        
        setTimeout(() => {
          setShowPayment(false);
          setClientSecret('');
          navigate('/success');
        }, 1500);

      } else {
        throw new Error('Payment not confirmed by backend');
      }

    } catch (error) {
      console.error('‚ùå Post-payment processing error:', error);
      const errorMessage = error.response?.data?.error || error.message;
      setPaymentError(`Payment successful but order processing failed: ${errorMessage}`);
    }
  };

  const handlePaymentError = (errorMessage) => {
    console.error('‚ùå Payment error:', errorMessage);
    setPaymentError(`Payment failed: ${errorMessage}`);
  };

  const handleCheckout = () => {
    if (cartItems.length === 0) {
      alert('Your cart is empty');
      return;
    }
    
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to checkout');
      navigate('/login');
      return;
    }

    if (!showAddressForm) {
      setShowAddressForm(true);
      return;
    }

    // Validate address before proceeding to payment
    if (!address.street || !address.city || !address.state || !address.zipCode || !address.country) {
      alert('Please fill in all address fields');
      return;
    }

    setShowPayment(true);
    handlePayment(); // Start the payment process
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setAddress(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleContinueShopping = () => {
    navigate('/');
  };

  const shippingInfo = calculateShipping();

  if (loading) {
    return (
      <Container>
        <Wrapper>
          <Title>YOUR BAG</Title>
          <div style={{ textAlign: 'center', padding: '50px' }}>Loading cart...</div>
        </Wrapper>
      </Container>
    );
  }

  return (
    <Container>
      <Wrapper>
        <Title>YOUR BAG</Title>
        <Top>
          <TopButton onClick={handleContinueShopping}>CONTINUE SHOPPING</TopButton>
          <TopTexts>
            <TopText>Shopping Bag({cartItems.length})</TopText>
            <TopText>Your Wishlist (0)</TopText>
          </TopTexts>
          <TopButton 
            type="filled" 
            onClick={handleCheckout}
            disabled={cartItems.length === 0}
          >
            {isCheckingOut ? 'PROCESSING...' : 'CHECKOUT NOW'}
          </TopButton>
        </Top>
        <Bottom>
          <Info>
            {cartItems.length === 0 ? (
              <EmptyCart>
                <h3>Your cart is empty</h3>
                <p>Add some products to your cart to see them here</p>
                <TopButton onClick={handleContinueShopping} style={{ marginTop: '20px' }}>
                  START SHOPPING
                </TopButton>
              </EmptyCart>
            ) : (
              cartItems.map((item) => (
                <div key={item.id}>
                  <Product>
                    <ProductDetail>
                      <Image src={item.img || 'https://via.placeholder.com/200x200?text=No+Image'} />
                      <Details>
                        <ProductName>
                          <b>Product:</b> {item.title}
                        </ProductName>
                        <ProductId>
                          <b>ID:</b> {item.productId}
                        </ProductId>
                        {item.size && (
                          <div>
                            <b>Size:</b> {item.size}
                          </div>
                        )}
                        {item.color && (
                          <div>
                            <b>Color:</b> {item.color}
                          </div>
                        )}
                      </Details>
                    </ProductDetail>
                    <PriceDetail>
                      <ProductAmountContainer>
                        <QuantityButton onClick={() => handleQuantity(item.productId, item.quantity - 1)}>
                          -
                        </QuantityButton>
                        <ProductAmount>{item.quantity}</ProductAmount>
                        <QuantityButton onClick={() => handleQuantity(item.productId, item.quantity + 1)}>
                          +
                        </QuantityButton>
                      </ProductAmountContainer>
                      <ProductPrice>$ {(parseFloat(item.price) * item.quantity).toFixed(2)}</ProductPrice>
                    </PriceDetail>
                  </Product>
                  <Hr />
                </div>
              ))
            )}
          </Info>
          {cartItems.length > 0 && (
            <Summary>
              <SummaryTitle>ORDER SUMMARY</SummaryTitle>
              <SummaryItem>
                <SummaryItemText>Subtotal</SummaryItemText>
                <SummaryItemPrice>$ {getTotal().toFixed(2)}</SummaryItemPrice>
              </SummaryItem>
              
              <SummaryItem>
                <SummaryItemText>
                  Shipping
                  {shippingInfo.hasFreeShipping && (
                    <FreeShippingBadge>üöö FREE</FreeShippingBadge>
                  )}
                </SummaryItemText>
                <SummaryItemPrice>
                  {shippingInfo.hasFreeShipping ? '$ 0.00' : `$ ${shippingInfo.shipping.toFixed(2)}`}
                </SummaryItemPrice>
              </SummaryItem>

              {!shippingInfo.hasFreeShipping && (
                <SummaryItem>
                  <SummaryItemText style={{ fontSize: '12px', color: '#666', fontStyle: 'italic' }}>
                    Add ${(50 - getTotal()).toFixed(2)} more for free shipping!
                  </SummaryItemText>
                  <SummaryItemPrice></SummaryItemPrice>
                </SummaryItem>
              )}

              <SummaryItem type="total">
                <SummaryItemText>Total</SummaryItemText>
                <SummaryItemPrice>$ {getFinalTotal().toFixed(2)}</SummaryItemPrice>
              </SummaryItem>

              {showAddressForm && (
                <AddressForm>
                  <h3>Shipping Address</h3>
                  <Input
                    type="text"
                    name="street"
                    placeholder="Street Address"
                    value={address.street}
                    onChange={handleInputChange}
                    required
                  />
                  <InputRow>
                    <InputGroup>
                      <Input
                        type="text"
                        name="city"
                        placeholder="City"
                        value={address.city}
                        onChange={handleInputChange}
                        required
                      />
                    </InputGroup>
                    <InputGroup>
                      <Input
                        type="text"
                        name="state"
                        placeholder="State"
                        value={address.state}
                        onChange={handleInputChange}
                        required
                      />
                    </InputGroup>
                  </InputRow>
                  <InputRow>
                    <InputGroup>
                      <Input
                        type="text"
                        name="zipCode"
                        placeholder="ZIP Code"
                        value={address.zipCode}
                        onChange={handleInputChange}
                        required
                      />
                    </InputGroup>
                    <InputGroup>
                      <Input
                        type="text"
                        name="country"
                        placeholder="Country"
                        value={address.country}
                        onChange={handleInputChange}
                        required
                      />
                    </InputGroup>
                  </InputRow>
                </AddressForm>
              )}

              <Button 
                onClick={handleCheckout}
                disabled={cartItems.length === 0 || isCheckingOut}
                style={{
                  backgroundColor: cartItems.length === 0 ? '#ccc' : isCheckingOut ? '#ffa500' : '#000'
                }}
              >
                {isCheckingOut ? 'üîÑ PROCESSING...' : 
                 showAddressForm ? 'üí≥ PROCEED TO PAYMENT' : 
                 `üõí CHECKOUT NOW ($${getFinalTotal().toFixed(2)})`}
              </Button>
            </Summary>
          )}
        </Bottom>
      </Wrapper>

      {showPayment && (
        <PaymentModal>
          <PaymentContent>
            <h2 style={{ marginBottom: '10px', color: '#333' }}>Complete Your Purchase</h2>
            <p style={{ fontSize: '18px', fontWeight: '600', color: '#2c5aa0', marginBottom: '20px' }}>
              Total Amount: <span style={{ color: '#4CAF50' }}>${getFinalTotal().toFixed(2)}</span>
            </p>
            
            {paymentError && (
              <ErrorMessage>
                <strong>Payment Error:</strong> {paymentError}
                <br />
                <small>Please check your address information and try again.</small>
              </ErrorMessage>
            )}
            {paymentSuccess && <SuccessMessage>{paymentSuccess}</SuccessMessage>}
            
            {!clientSecret && isCheckingOut && (
              <LoadingSpinner>
                <div style={{ marginBottom: '10px' }}>üîÑ Setting up payment...</div>
                <div style={{ fontSize: '14px', color: '#666' }}>
                  Creating order and payment session...
                </div>
              </LoadingSpinner>
            )}
            
            {clientSecret ? (
              <Elements stripe={stripePromise} options={{ clientSecret }}>
                <StripePaymentForm 
                  amount={getFinalTotal().toFixed(2)}
                  clientSecret={clientSecret}
                  onSuccess={handlePaymentSuccess}
                  onError={handlePaymentError}
                />
              </Elements>
            ) : (
              !isCheckingOut && (
                <div>
                  <PayNowBtn 
                    onClick={handlePayment} 
                    disabled={isCheckingOut}
                  >
                    {isCheckingOut ? 'Setting Up Payment...' : 'Try Payment Again'}
                  </PayNowBtn>
                </div>
              )
            )}
            
            <CancelBtn onClick={() => {
              setShowPayment(false);
              setClientSecret('');
              setPaymentError('');
              setPaymentSuccess('');
              setIsCheckingOut(false);
            }}>
              Cancel Payment
            </CancelBtn>
          </PaymentContent>
        </PaymentModal>
      )}
    </Container>
  );
};

export default CartPage;
