import React, { useState, useEffect, useCallback } from "react";
import styled from "styled-components";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";
import { 
  Elements, 
  CardNumberElement, 
  CardExpiryElement, 
  CardCvcElement, 
  useStripe, 
  useElements 
} from "@stripe/react-stripe-js";

const stripePromise = loadStripe("pk_test_51SEWfbGUJ7v3D9GXVs5sPIBEY08wZrpGmeuko8YClRovgbeQkkYJ2NpxLMtM3RyA8v7NSKM4cE8mTLthWafiYGv500lPgVJb3n");

// Mobile responsive styles
const Container = styled.div`
  width: 100%; 
  min-height: 100vh;
`;

const Wrapper = styled.div`
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
  
  @media (max-width: 768px) {
    padding: 10px;
  }
`;

const Title = styled.h1`
  font-weight: 300;
  text-align: center;
  font-size: 1.8rem;
  margin-bottom: 20px;
  
  @media (max-width: 768px) {
    font-size: 1.5rem;
  }
  
  @media (max-width: 480px) {
    font-size: 1.3rem;
    margin-bottom: 15px;
  }
`;

const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
  flex-wrap: wrap;
  gap: 15px;
  
  @media (max-width: 768px) {
    padding: 15px 10px;
    justify-content: center;
  }
  
  @media (max-width: 480px) {
    flex-direction: column;
    gap: 10px;
    padding: 10px 5px;
  }
`;

const TopButton = styled.button`
  padding: 12px 20px;
  font-weight: 600;
  cursor: pointer;
  border: ${(props) => props.type === "filled" ? "none" : "2px solid black"};
  background-color: ${(props) => props.type === "filled" ? "black" : "transparent"};
  color: ${(props) => props.type === "filled" ? "white" : "black"};
  border-radius: 8px;
  font-size: 14px;
  min-height: 44px;
  
  &:disabled {
    background-color: #ccc;
    cursor: not-allowed;
  }
  
  @media (max-width: 480px) {
    padding: 10px 16px;
    font-size: 13px;
    min-height: 40px;
    width: 100%;
    max-width: 200px;
  }
`;

const TopTexts = styled.div`
  display: flex;
  gap: 15px;
  
  @media (max-width: 480px) {
    gap: 10px;
    justify-content: center;
    width: 100%;
  }
`;

const TopText = styled.span`
  text-decoration: underline;
  cursor: pointer;
  font-size: 14px;
  
  @media (max-width: 480px) {
    font-size: 13px;
  }
`;

const Bottom = styled.div`
  display: flex;
  justify-content: space-between;
  gap: 30px;
  
  @media (max-width: 768px) {
    flex-direction: column;
    gap: 20px;
  }
`;

const Info = styled.div`flex: 3;`;

const Product = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
  gap: 20px;
  padding: 15px;
  border: 1px solid #f0f0f0;
  border-radius: 10px;
  
  @media (max-width: 768px) {
    flex-direction: column;
    gap: 15px;
    padding: 12px;
  }
`;

const ProductDetail = styled.div`
  flex: 2;
  display: flex;
  gap: 15px;
  
  @media (max-width: 480px) {
    flex-direction: column;
    text-align: center;
    gap: 10px;
  }
`;

const Image = styled.img`
  width: 200px;
  height: 200px;
  object-fit: cover;
  border-radius: 8px;
  
  @media (max-width: 768px) {
    width: 150px;
    height: 150px;
  }
  
  @media (max-width: 480px) {
    width: 100%;
    max-width: 250px;
    height: 180px;
    margin: 0 auto;
  }
`;

const Details = styled.div`
  padding: 10px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  flex: 1;
  
  @media (max-width: 480px) {
    padding: 8px 0;
    gap: 8px;
  }
`;

const ProductName = styled.span`
  font-size: 16px;
  font-weight: 500;
  line-height: 1.4;
  
  @media (max-width: 480px) {
    font-size: 15px;
  }
`;

const ProductId = styled.span`
  color: gray;
  font-size: 12px;
`;

const PriceDetail = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 15px;
  
  @media (max-width: 768px) {
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
  }
`;

const ProductAmountContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
`;

const ProductAmount = styled.div`
  font-size: 20px;
  margin: 0 10px;
  min-width: 30px;
  text-align: center;
  
  @media (max-width: 480px) {
    font-size: 18px;
  }
`;

const ProductPrice = styled.div`
  font-size: 24px;
  font-weight: 200;
  
  @media (max-width: 480px) {
    font-size: 20px;
  }
`;

const Hr = styled.hr`
  background-color: #eee;
  border: none;
  height: 1px;
  margin: 20px 0;
`;

const Summary = styled.div`
  flex: 1;
  border: 0.5px solid lightgray;
  border-radius: 10px;
  padding: 20px;
  height: fit-content;
  position: sticky;
  top: 20px;
  
  @media (max-width: 768px) {
    position: static;
    margin-top: 20px;
  }
`;

const SummaryTitle = styled.h1`
  font-weight: 200;
  font-size: 1.5rem;
  margin-bottom: 20px;
  
  @media (max-width: 480px) {
    font-size: 1.25rem;
    margin-bottom: 15px;
  }
`;

const SummaryItem = styled.div`
  margin: 20px 0px;
  display: flex;
  justify-content: space-between;
  font-weight: ${(props) => props.type === "total" && "500"};
  font-size: ${(props) => props.type === "total" ? "20px" : "16px"};
  
  @media (max-width: 480px) {
    margin: 15px 0px;
    font-size: ${(props) => props.type === "total" ? "18px" : "14px"};
  }
`;

const Button = styled.button`
  width: 100%;
  padding: 15px;
  background-color: black;
  color: white;
  font-weight: 600;
  border: none;
  cursor: pointer;
  margin-top: 10px;
  border-radius: 8px;
  font-size: 16px;
  min-height: 50px;
  
  &:disabled {
    background-color: #ccc;
    cursor: not-allowed;
  }
  
  @media (max-width: 480px) {
    padding: 12px;
    font-size: 14px;
    min-height: 44px;
  }
`;

const QuantityButton = styled.button`
  width: 35px;
  height: 35px;
  border: 1px solid teal;
  background-color: white;
  cursor: pointer;
  font-size: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 4px;
  min-height: 35px;
  
  @media (max-width: 480px) {
    width: 30px;
    height: 30px;
    font-size: 14px;
  }
`;

const EmptyCart = styled.div`
  text-align: center;
  padding: 50px;
  font-size: 18px;
  color: gray;
  
  @media (max-width: 480px) {
    padding: 30px 20px;
    font-size: 16px;
  }
`;

const AddressForm = styled.div`
  margin: 20px 0;
  padding: 20px;
  border: 1px solid #eee;
  border-radius: 10px;
  background-color: #f9f9f9;
  
  @media (max-width: 480px) {
    padding: 15px;
  }
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  margin: 8px 0;
  border: 1px solid #ddd;
  border-radius: 4px;
  font-size: 14px;
  
  @media (max-width: 480px) {
    padding: 10px;
    font-size: 16px;
  }
`;

const InputRow = styled.div`
  display: flex;
  gap: 10px;
  
  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const InputGroup = styled.div`flex: 1;`;

const PaymentModal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
  padding: 20px;
  
  @media (max-width: 480px) {
    padding: 10px;
    align-items: flex-start;
    padding-top: 50px;
  }
`;

const PaymentContent = styled.div`
  background: white;
  padding: 2rem;
  border-radius: 12px;
  width: 90%;
  max-width: 500px;
  text-align: center;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
  max-height: 90vh;
  overflow-y: auto;
  
  @media (max-width: 480px) {
    padding: 1.5rem;
    max-height: 80vh;
  }
`;

const PayNowBtn = styled.button`
  background: #4CAF50;
  color: white;
  padding: 12px 24px;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin: 5px;
  font-size: 16px;
  font-weight: 600;
  width: 100%;
  
  &:disabled {
    background: #cccccc;
    cursor: not-allowed;
  }
`;

const CancelBtn = styled.button`
  background: #f44336;
  color: white;
  padding: 12px 24px;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin: 5px;
  font-size: 16px;
  font-weight: 600;
  width: 100%;
`;

const ErrorMessage = styled.div`
  color: #d32f2f;
  background-color: #ffebee;
  padding: 12px;
  border-radius: 6px;
  margin: 15px 0;
  font-size: 14px;
  border: 1px solid #f5c6cb;
`;

const SuccessMessage = styled.div`
  color: #155724;
  background-color: #d4edda;
  padding: 12px;
  border-radius: 6px;
  margin: 15px 0;
  font-size: 14px;
  border: 1px solid #c3e6cb;
`;

const LoadingSpinner = styled.div`
  text-align: center;
  padding: 20px;
  color: #666;
`;

// Stripe Payment Form Component
const StripePaymentForm = ({ amount, clientSecret, onSuccess, onError }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    if (!stripe || !elements) return;

    setProcessing(true);
    setError('');

    try {
      const cardNumber = elements.getElement(CardNumberElement);
      const { error: stripeError, paymentMethod } = await stripe.createPaymentMethod({
        type: 'card',
        card: cardNumber,
      });

      if (stripeError) {
        setError(stripeError.message);
        onError(stripeError.message);
        return;
      }

      const { error: confirmError, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: paymentMethod.id,
      });

      if (confirmError) {
        setError(confirmError.message);
        onError(confirmError.message);
      } else if (paymentIntent.status === 'succeeded') {
        onSuccess(paymentIntent);
      }
    } catch (err) {
      setError('Payment failed. Please try again.');
      onError(err.message);
    } finally {
      setProcessing(false);
    }
  };

  return (
    <div>
      <h3>Payment Details</h3>
      <p>Total Amount: ${amount}</p>
      
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '15px' }}>
          <label>Card Number</label>
          <div style={{ padding: '12px', border: '2px solid #e1e5e9', borderRadius: '8px' }}>
            <CardNumberElement options={{ placeholder: '1234 1234 1234 1234' }} />
          </div>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px', marginBottom: '15px' }}>
          <div>
            <label>Expiry Date</label>
            <div style={{ padding: '12px', border: '2px solid #e1e5e9', borderRadius: '8px' }}>
              <CardExpiryElement options={{ placeholder: 'MM/YY' }} />
            </div>
          </div>

          <div>
            <label>CVC</label>
            <div style={{ padding: '12px', border: '2px solid #e1e5e9', borderRadius: '8px' }}>
              <CardCvcElement options={{ placeholder: '123' }} />
            </div>
          </div>
        </div>
        
        {error && <ErrorMessage>{error}</ErrorMessage>}
        
        <PayNowBtn type="submit" disabled={!stripe || processing}>
          {processing ? 'Processing Payment...' : `Pay $${amount}`}
        </PayNowBtn>
      </form>
    </div>
  );
};

// Main Cart Page Component
const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isCheckingOut, setIsCheckingOut] = useState(false);
  const [showAddressForm, setShowAddressForm] = useState(false);
  const [showPayment, setShowPayment] = useState(false);
  const [paymentError, setPaymentError] = useState('');
  const [paymentSuccess, setPaymentSuccess] = useState('');
  const [clientSecret, setClientSecret] = useState('');
  const [currentOrderId, setCurrentOrderId] = useState('');
  const navigate = useNavigate();

  const [address, setAddress] = useState({
    street: "", city: "", state: "", zipCode: "", country: "India" // Fixed country
  });

  // Helper Functions
  const getAuthHeader = () => {
    const token = localStorage.getItem('token');
    return token ? { Authorization: `Bearer ${token}` } : {};
  };

  const getCurrentUser = () => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const base64Payload = token.split('.')[1];
        const payload = JSON.parse(atob(base64Payload));
        return payload.userId;
      } catch (error) {
        return null;
      }
    }
    return null;
  };

  const getTotal = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price) * item.quantity, 0);
  };

  const getFinalTotal = () => {
    const subtotal = getTotal();
    const shipping = subtotal >= 50 ? 0 : 5.90;
    return subtotal + shipping;
  };

  // SIMPLE CART CLEARING
  const clearCartAfterPayment = async () => {
    const userId = getCurrentUser();
    if (!userId) return false;

    try {
      console.log('ðŸ›’ Clearing cart after payment...');
      await axios.delete(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}/clear`,
        { headers: getAuthHeader() }
      );
      setCartItems([]);
      console.log('âœ… Cart cleared successfully');
      return true;
    } catch (error) {
      console.error('âŒ Error clearing cart:', error);
      setCartItems([]);
      return true;
    }
  };

  // UPDATE ORDER STATUS AFTER PAYMENT
  const updateOrderStatus = async (orderId, paymentIntentId) => {
    try {
      console.log('ðŸ”„ Updating order status...');
      const response = await axios.put(
        `${process.env.REACT_APP_API_URL}/api/orders/${orderId}/status`,
        { 
          status: 'confirmed',
          paymentStatus: 'paid',
          paymentIntentId: paymentIntentId
        },
        { headers: getAuthHeader() }
      );
      console.log('âœ… Order status updated:', response.data);
      return true;
    } catch (error) {
      console.error('âŒ Error updating order status:', error);
      return false;
    }
  };

  const fetchCart = useCallback(async () => {
    const userId = getCurrentUser();
    if (!userId) {
      setCartItems([]);
      return;
    }

    try {
      setLoading(true);
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        { headers: getAuthHeader() }
      );
      setCartItems(response.data);
    } catch (error) {
      console.error('Error fetching cart:', error);
      setCartItems([]);
    } finally {
      setLoading(false);
    }
  }, []);

  const updateCartItem = async (productId, quantity) => {
    const userId = getCurrentUser();
    if (!userId) return;

    try {
      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        { productId, quantity },
        { headers: getAuthHeader() }
      );
      await fetchCart();
    } catch (error) {
      console.error('Error updating cart:', error);
    }
  };

  const removeFromCart = async (productId) => {
    const userId = getCurrentUser();
    if (!userId) return;

    try {
      await axios.delete(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}/${productId}`,
        { headers: getAuthHeader() }
      );
      await fetchCart();
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  };

  useEffect(() => {
    fetchCart();
  }, [fetchCart]);

  const handleQuantity = (productId, quantity) => {
    if (quantity === 0) {
      removeFromCart(productId);
    } else {
      updateCartItem(productId, quantity);
    }
  };

  const handlePayment = async () => {
    setPaymentError('');
    setPaymentSuccess('');
    setIsCheckingOut(true);

    try {
      const userId = getCurrentUser();
      const token = localStorage.getItem('token');
      const BASE_URL = process.env.REACT_APP_API_URL;

      if (!address.street || !address.city || !address.state || !address.zipCode) {
        throw new Error('Please fill in all address fields');
      }

      if (cartItems.length === 0) {
        throw new Error('Cart is empty');
      }

      console.log('ðŸ”„ Starting payment process...');

      // Create order
      const orderData = {
        cartItems: cartItems.map(item => ({
          productId: item.productId,
          title: item.title,
          price: parseFloat(item.price),
          quantity: item.quantity,
          size: item.size,
          color: item.color,
          img: item.img
        })),
        address: address,
        totalAmount: getFinalTotal()
      };

      const orderResponse = await axios.post(
        `${BASE_URL}/api/orders`,
        orderData,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (!orderResponse.data.order?.id) {
        throw new Error('Order creation failed');
      }

      const orderId = orderResponse.data.order.id;
      setCurrentOrderId(orderId);

      // Create payment intent
      const amount = Math.round(getFinalTotal() * 100);
      const paymentResponse = await axios.post(
        `${BASE_URL}/api/payments/create-payment-intent`,
        { orderId, amount, currency: 'usd' },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (paymentResponse.data.clientSecret) {
        setClientSecret(paymentResponse.data.clientSecret);
      } else {
        throw new Error('No client secret received');
      }

    } catch (error) {
      console.error('Payment setup error:', error);
      setPaymentError(error.response?.data?.error || error.message);
      setIsCheckingOut(false);
    }
  };

  // FIXED PAYMENT SUCCESS HANDLER
  const handlePaymentSuccess = async (paymentIntent) => {
    try {
      console.log('âœ… Payment successful!');
      
      // STEP 1: Update order status to PAID
      console.log('ðŸ”„ Updating order status to PAID...');
      await updateOrderStatus(currentOrderId, paymentIntent.id);
      
      // STEP 2: Clear cart
      console.log('ðŸ›’ Clearing cart...');
      await clearCartAfterPayment();
      
      // STEP 3: Save order data for success page
      const orderDataForSuccess = {
        id: currentOrderId,
        amount: getFinalTotal().toFixed(2),
        products: cartItems,
        address: address,
        paymentIntentId: paymentIntent.id,
        status: 'confirmed',
        paymentStatus: 'paid'
      };
      localStorage.setItem('lastOrder', JSON.stringify(orderDataForSuccess));
      
      console.log('ðŸŽ‰ Payment completed, redirecting...');
      
      // STEP 4: Redirect to success page
      setShowPayment(false);
      navigate('/success');
      
    } catch (error) {
      console.error('Post-payment error:', error);
      // Still redirect even if there are minor errors
      setShowPayment(false);
      navigate('/success');
    }
  };

  const handlePaymentError = (errorMessage) => {
    console.error('Payment error:', errorMessage);
    setPaymentError(`Payment failed: ${errorMessage}`);
  };

  const handleCheckout = () => {
    if (cartItems.length === 0) {
      alert('Your cart is empty');
      return;
    }
    
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to checkout');
      navigate('/login');
      return;
    }

    if (!showAddressForm) {
      setShowAddressForm(true);
      return;
    }

    if (!address.street || !address.city || !address.state || !address.zipCode) {
      alert('Please fill in all address fields');
      return;
    }

    setShowPayment(true);
    handlePayment();
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setAddress(prev => ({ ...prev, [name]: value }));
  };

  const handleContinueShopping = () => {
    navigate('/');
  };

  if (loading) {
    return (
      <Container>
        <Wrapper>
          <Title>YOUR BAG</Title>
          <div style={{ textAlign: 'center', padding: '50px' }}>Loading cart...</div>
        </Wrapper>
      </Container>
    );
  }

  return (
    <Container>
      <Wrapper>
        <Title>YOUR BAG</Title>
        <Top>
          <TopButton onClick={handleContinueShopping}>CONTINUE SHOPPING</TopButton>
          <TopTexts>
            <TopText>Shopping Bag({cartItems.length})</TopText>
          </TopTexts>
          <TopButton 
            type="filled" 
            onClick={handleCheckout}
            disabled={cartItems.length === 0}
          >
            {isCheckingOut ? 'PROCESSING...' : 'CHECKOUT NOW'}
          </TopButton>
        </Top>
        <Bottom>
          <Info>
            {cartItems.length === 0 ? (
              <EmptyCart>
                <h3>Your cart is empty</h3>
                <p>Add some products to your cart to see them here</p>
                <TopButton onClick={handleContinueShopping} style={{ marginTop: '20px' }}>
                  START SHOPPING
                </TopButton>
              </EmptyCart>
            ) : (
              cartItems.map((item) => (
                <div key={item.id}>
                  <Product>
                    <ProductDetail>
                      <Image src={item.img || 'https://via.placeholder.com/200x200?text=No+Image'} />
                      <Details>
                        <ProductName><b>Product:</b> {item.title}</ProductName>
                        <ProductId><b>ID:</b> {item.productId}</ProductId>
                        {item.size && <div><b>Size:</b> {item.size}</div>}
                        {item.color && <div><b>Color:</b> {item.color}</div>}
                      </Details>
                    </ProductDetail>
                    <PriceDetail>
                      <ProductAmountContainer>
                        <QuantityButton onClick={() => handleQuantity(item.productId, item.quantity - 1)}>-</QuantityButton>
                        <ProductAmount>{item.quantity}</ProductAmount>
                        <QuantityButton onClick={() => handleQuantity(item.productId, item.quantity + 1)}>+</QuantityButton>
                      </ProductAmountContainer>
                      <ProductPrice>$ {(parseFloat(item.price) * item.quantity).toFixed(2)}</ProductPrice>
                    </PriceDetail>
                  </Product>
                  <Hr />
                </div>
              ))
            )}
          </Info>
          {cartItems.length > 0 && (
            <Summary>
              <SummaryTitle>ORDER SUMMARY</SummaryTitle>
              <SummaryItem>
                <span>Subtotal</span>
                <span>$ {getTotal().toFixed(2)}</span>
              </SummaryItem>
              <SummaryItem>
                <span>Shipping</span>
                <span>{getTotal() >= 50 ? '$ 0.00' : '$ 5.90'}</span>
              </SummaryItem>
              <SummaryItem type="total">
                <span><b>Total</b></span>
                <span><b>$ {getFinalTotal().toFixed(2)}</b></span>
              </SummaryItem>

              {showAddressForm && (
                <AddressForm>
                  <h3>Shipping Address</h3>
                  <Input type="text" name="street" placeholder="Street Address" value={address.street} onChange={handleInputChange} required />
                  <Input type="text" name="city" placeholder="City" value={address.city} onChange={handleInputChange} required />
                  <Input type="text" name="state" placeholder="State" value={address.state} onChange={handleInputChange} required />
                  <Input type="text" name="zipCode" placeholder="ZIP Code" value={address.zipCode} onChange={handleInputChange} required />
                  <Input type="text" name="country" placeholder="Country" value={address.country} onChange={handleInputChange} required />
                </AddressForm>
              )}

              <Button onClick={handleCheckout} disabled={cartItems.length === 0 || isCheckingOut}>
                {isCheckingOut ? 'PROCESSING...' : 'CHECKOUT NOW'}
              </Button>
            </Summary>
          )}
        </Bottom>
      </Wrapper>

      {showPayment && (
        <PaymentModal>
          <PaymentContent>
            <h2>Complete Your Payment</h2>
            
            {paymentError && <ErrorMessage>{paymentError}</ErrorMessage>}
            {paymentSuccess && <SuccessMessage>{paymentSuccess}</SuccessMessage>}

            {!paymentSuccess && clientSecret ? (
              <Elements stripe={stripePromise} options={{ clientSecret }}>
                <StripePaymentForm
                  amount={getFinalTotal().toFixed(2)}
                  clientSecret={clientSecret}
                  onSuccess={handlePaymentSuccess}
                  onError={handlePaymentError}
                />
              </Elements>
            ) : !clientSecret && !paymentError && !paymentSuccess ? (
              <LoadingSpinner>â³ Setting up payment...</LoadingSpinner>
            ) : null}

            <CancelBtn onClick={() => {
              setShowPayment(false);
              setClientSecret('');
              setPaymentError('');
              setPaymentSuccess('');
              setIsCheckingOut(false);
            }}>
              Cancel Payment
            </CancelBtn>
          </PaymentContent>
        </PaymentModal>
      )}
    </Container>
  );
};

export default CartPage;
