import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';

const PageContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  padding: 20px;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  background: white;
  border-radius: 20px;
  box-shadow: 0 10px 40px rgba(0,0,0,0.1);
  overflow: hidden;
`;

const Header = styled.div`
  padding: 30px 40px 0;
  display: flex;
  align-items: center;
  gap: 15px;
`;

const BackButton = styled.button`
  padding: 12px 20px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  border-radius: 10px;
  cursor: pointer;
  font-size: 14px;
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 8px;
  transition: all 0.3s ease;
  
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(102, 126, 234, 0.4);
  }
`;

const Content = styled.div`
  padding: 40px;
  display: flex;
  gap: 60px;
  flex-wrap: wrap;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 40px;
    padding: 30px 20px;
  }
`;

const ImageContainer = styled.div`
  flex: 1;
  min-width: 300px;
  display: flex;
  flex-direction: column;
  gap: 20px;
  padding: 20px;
  background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
  border-radius: 15px;
`;

const MainImage = styled.img`
  width: 100%;
  max-width: 500px;
  height: 400px;
  object-fit: contain;
  border-radius: 10px;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.02);
  }
`;

const ColorThumbnails = styled.div`
  display: flex;
  gap: 10px;
  flex-wrap: wrap;
  justify-content: center;
`;

const ColorThumbnail = styled.img`
  width: 60px;
  height: 60px;
  object-fit: cover;
  border-radius: 8px;
  cursor: pointer;
  border: 3px solid ${props => props.selected ? '#667eea' : 'transparent'};
  transition: all 0.3s ease;

  &:hover {
    transform: scale(1.1);
    border-color: #667eea;
  }
`;

const InfoContainer = styled.div`
  flex: 1;
  min-width: 300px;
  padding: 20px 0;
`;

const Title = styled.h1`
  font-weight: 700;
  font-size: 2.8rem;
  margin-bottom: 20px;
  color: #2d3748;
  line-height: 1.2;
`;

const Category = styled.div`
  display: inline-block;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  padding: 6px 16px;
  border-radius: 20px;
  font-size: 0.9rem;
  font-weight: 600;
  margin-bottom: 25px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
`;

const Desc = styled.p`
  margin: 25px 0;
  font-size: 1.1rem;
  line-height: 1.7;
  color: #4a5568;
  background: #f8f9fa;
  padding: 25px;
  border-radius: 12px;
  border-left: 4px solid #667eea;
`;

const PriceSection = styled.div`
  margin: 30px 0;
  padding: 25px;
  background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
  border-radius: 12px;
  text-align: center;
`;

const PriceLabel = styled.div`
  font-size: 1rem;
  color: #718096;
  margin-bottom: 8px;
  font-weight: 600;
`;

const Price = styled.span`
  font-weight: 700;
  font-size: 3rem;
  color: #2d3748;
  display: block;
`;

const InStock = styled.div`
  display: inline-flex;
  align-items: center;
  gap: 8px;
  background: #48bb78;
  color: white;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 0.9rem;
  font-weight: 600;
  margin-top: 10px;
`;

const FilterContainer = styled.div`
  margin: 35px 0;
  display: flex;
  flex-direction: column;
  gap: 25px;
`;

const Filter = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
  padding: 20px;
  background: #f8f9fa;
  border-radius: 12px;
  border: 2px solid #e2e8f0;
  transition: all 0.3s ease;

  &:hover {
    border-color: #667eea;
    transform: translateY(-2px);
  }
`;

const FilterTitle = styled.span`
  font-size: 1.1rem;
  font-weight: 600;
  color: #2d3748;
  min-width: 80px;
`;

const Select = styled.select`
  padding: 12px 20px;
  border: 2px solid #e2e8f0;
  border-radius: 10px;
  font-size: 1rem;
  background: white;
  min-width: 200px;
  transition: all 0.3s ease;
  cursor: pointer;

  &:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  }
`;

const AddContainer = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 40px;
  flex-wrap: wrap;
`;

const Button = styled.button`
  padding: 18px 35px;
  border: 2px solid;
  border-radius: 12px;
  cursor: pointer;
  font-weight: 600;
  font-size: 1.1rem;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 10px;
  flex: 1;
  min-width: 200px;
  justify-content: center;

  ${props => props.primary ? `
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    border-color: transparent;
    
    &:hover {
      transform: translateY(-3px);
      box-shadow: 0 8px 25px rgba(102, 126, 234, 0.4);
    }
  ` : `
    background: white;
    color: #667eea;
    border-color: #667eea;
    
    &:hover {
      background: #667eea;
      color: white;
      transform: translateY(-3px);
      box-shadow: 0 8px 25px rgba(102, 126, 234, 0.3);
    }
  `}

  &:active {
    transform: translateY(-1px);
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none !important;
    box-shadow: none !important;
  }
`;

const SuccessMessage = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: rgba(72, 187, 120, 0.95);
  color: white;
  padding: 20px 30px;
  border-radius: 15px;
  font-size: 1.1rem;
  font-weight: 600;
  z-index: 1000;
  display: flex;
  align-items: center;
  gap: 10px;
  animation: fadeInOut 2s ease-in-out;
  box-shadow: 0 10px 30px rgba(72, 187, 120, 0.4);
  
  @keyframes fadeInOut {
    0% { opacity: 0; transform: translate(-50%, -50%) scale(0.8); }
    20% { opacity: 1; transform: translate(-50%, -50%) scale(1); }
    80% { opacity: 1; transform: translate(-50%, -50%) scale(1); }
    100% { opacity: 0; transform: translate(-50%, -50%) scale(0.8); }
  }
`;

const LoadingContainer = styled.div`
  text-align: center;
  padding: 100px 20px;
  color: #718096;
  font-size: 1.2rem;
`;

const ColorOption = styled.option`
  display: flex;
  align-items: center;
  gap: 8px;
`;

const ProductPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState({});
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [currentImage, setCurrentImage] = useState('');
  const [loading, setLoading] = useState(true);
  const [isAdding, setIsAdding] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  
  useEffect(() => {
    const getProduct = async () => {
      try {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/products/${id}`);
        setProduct(res.data);
        setSelectedSize(res.data.size && res.data.size.length > 0 ? res.data.size[0] : '');
        const defaultColor = res.data.color && res.data.color.length > 0 ? res.data.color[0] : '';
        setSelectedColor(defaultColor);
        
        // Set initial image based on colorImages or fallback to main image
        if (res.data.colorImages && res.data.colorImages[defaultColor]) {
          setCurrentImage(res.data.colorImages[defaultColor]);
        } else {
          setCurrentImage(res.data.img);
        }
        
        setLoading(false);
      } catch (err) {
        console.log(err);
        setLoading(false);
      }
    };
    getProduct();
  }, [id]);

  // Update image when color changes
  useEffect(() => {
    if (product.colorImages && product.colorImages[selectedColor]) {
      setCurrentImage(product.colorImages[selectedColor]);
    } else {
      // Fallback to main image if color image not available
      setCurrentImage(product.img);
    }
  }, [selectedColor, product.colorImages, product.img]);

  const addToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to add items to cart');
      return;
    }

    setIsAdding(true);

    try {
      const base64Payload = token.split('.')[1];
      const payload = JSON.parse(atob(base64Payload));
      const userId = payload.userId;

      const cartItem = {
        productId: product.id,
        quantity: 1,
        price: product.price,
        title: product.title,
        img: currentImage, // Use current image (color-specific)
        size: selectedSize,
        color: selectedColor
      };

      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        cartItem,
        { 
          headers: { 
            Authorization: `Bearer ${token}` 
          } 
        }
      );
      
      // Show success animation instead of alert
      setShowSuccess(true);
      setTimeout(() => setShowSuccess(false), 2000);
      
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Failed to add product to cart');
    } finally {
      setIsAdding(false);
    }
  };

  const handleColorChange = (color) => {
    setSelectedColor(color);
  };

  if (loading) {
    return (
      <PageContainer>
        <Container>
          <LoadingContainer>
            <i className="fas fa-spinner fa-spin" style={{ fontSize: '3rem', marginBottom: '20px', display: 'block', color: '#667eea' }}></i>
            Loading product details...
          </LoadingContainer>
        </Container>
      </PageContainer>
    );
  }

  return (
    <PageContainer>
      <Container>
        <Header>
          <BackButton onClick={() => navigate(-1)}>
            <i className="fas fa-arrow-left"></i>
            Back to Products
          </BackButton>
        </Header>
        
        <Content>
          <ImageContainer>
            <MainImage 
              src={currentImage} 
              alt={product.title}
              onError={(e) => {
                e.target.src = `https://via.placeholder.com/500x500/667eea/FFFFFF?text=${encodeURIComponent(product.title || 'Product')}`;
              }}
            />
            
            {/* Color Thumbnails */}
            {product.colorImages && Object.keys(product.colorImages).length > 0 && (
              <ColorThumbnails>
                {Object.entries(product.colorImages).map(([color, imageUrl]) => (
                  <ColorThumbnail
                    key={color}
                    src={imageUrl}
                    alt={color}
                    selected={selectedColor === color}
                    onClick={() => handleColorChange(color)}
                    onError={(e) => {
                      e.target.src = `https://via.placeholder.com/60x60/667eea/FFFFFF?text=${color.charAt(0).toUpperCase()}`;
                    }}
                  />
                ))}
              </ColorThumbnails>
            )}
          </ImageContainer>
          
          <InfoContainer>
            <Title>{product.title}</Title>
            
            {product.categories && product.categories.length > 0 && (
              <Category>
                {product.categories[0]}
              </Category>
            )}
            
            <Desc>{product.desc}</Desc>
            
            <PriceSection>
              <PriceLabel>PRICE</PriceLabel>
              <Price>${product.price}</Price>
              {product.inStock && (
                <InStock>
                  <i className="fas fa-check"></i>
                  In Stock
                </InStock>
              )}
            </PriceSection>
            
            <FilterContainer>
              {product.size && product.size.length > 0 && (
                <Filter>
                  <FilterTitle>
                    <i className="fas fa-ruler" style={{ marginRight: '8px' }}></i>
                    Size:
                  </FilterTitle>
                  <Select 
                    value={selectedSize} 
                    onChange={(e) => setSelectedSize(e.target.value)}
                  >
                    {product.size.map((size) => (
                      <option key={size} value={size}>{size}</option>
                    ))}
                  </Select>
                </Filter>
              )}
              
              {product.color && product.color.length > 0 && (
                <Filter>
                  <FilterTitle>
                    <i className="fas fa-palette" style={{ marginRight: '8px' }}></i>
                    Color:
                  </FilterTitle>
                  <Select 
                    value={selectedColor} 
                    onChange={(e) => setSelectedColor(e.target.value)}
                  >
                    {product.color.map((color) => (
                      <ColorOption key={color} value={color}>
                        {color}
                        {product.colorImages && product.colorImages[color] && ' ðŸŽ¨'}
                      </ColorOption>
                    ))}
                  </Select>
                </Filter>
              )}
            </FilterContainer>
            
            <AddContainer>
              <Button primary onClick={addToCart} disabled={isAdding}>
                {isAdding ? (
                  <>
                    <i className="fas fa-spinner fa-spin"></i>
                    Adding to Cart...
                  </>
                ) : (
                  <>
                    <i className="fas fa-shopping-cart"></i>
                    Add to Cart
                  </>
                )}
              </Button>
              <Button>
                <i className="fas fa-bolt"></i>
                Buy Now
              </Button>
            </AddContainer>
          </InfoContainer>
        </Content>
      </Container>

      {showSuccess && (
        <SuccessMessage>
          <i className="fas fa-check-circle"></i>
          Added to Cart Successfully!
        </SuccessMessage>
      )}
    </PageContainer>
  );
};

export default ProductPage;
