import React, { useState, useEffect, useCallback } from "react";
import styled from "styled-components";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";
import { 
  Elements, 
  CardNumberElement, 
  CardExpiryElement, 
  CardCvcElement, 
  useStripe, 
  useElements 
} from "@stripe/react-stripe-js";

const stripePromise = loadStripe("pk_test_51SEWfbGUJ7v3D9GXVs5sPIBEY08wZrpGmeuko8YClRovgbeQkkYJ2NpxLMtM3RyA8v7NSKM4cE8mTLthWafiYGv500lPgVJb3n");

// ========== ALL STYLED COMPONENTS ==========
const Container = styled.div``;

const Wrapper = styled.div`
  padding: 20px;
  
  @media (max-width: 768px) {
    padding: 10px;
  }
`;

const Title = styled.h1`
  font-weight: 300;
  text-align: center;
  font-size: 1.5rem;
  
  @media (min-width: 768px) {
    font-size: 2rem;
  }
`;

const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
  flex-wrap: wrap;
  gap: 15px;
  
  @media (max-width: 768px) {
    padding: 15px 10px;
    justify-content: center;
  }
`;

const TopButton = styled.button`
  padding: 12px 20px;
  font-weight: 600;
  cursor: pointer;
  border: ${(props) => props.type === "filled" && "none"};
  background-color: ${(props) =>
    props.type === "filled" ? "black" : "transparent"};
  color: ${(props) => props.type === "filled" && "white"};
  border-radius: 8px;
  font-size: 14px;
  min-height: 44px;
  
  @media (max-width: 480px) {
    padding: 10px 15px;
    font-size: 12px;
    min-height: 40px;
  }
`;

const TopTexts = styled.div`
  display: flex;
  gap: 10px;
  
  @media (max-width: 480px) {
    flex-direction: column;
    align-items: center;
    gap: 5px;
  }
`;

const TopText = styled.span`
  text-decoration: underline;
  cursor: pointer;
  font-size: 14px;
  
  @media (max-width: 480px) {
    font-size: 12px;
  }
`;

const Bottom = styled.div`
  display: flex;
  justify-content: space-between;
  gap: 20px;
  
  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const Info = styled.div`
  flex: 3;
`;

const Product = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
  gap: 15px;
  
  @media (max-width: 768px) {
    flex-direction: column;
    border: 1px solid #e0e0e0;
    border-radius: 10px;
    padding: 15px;
  }
`;

const ProductDetail = styled.div`
  flex: 2;
  display: flex;
  gap: 15px;
  
  @media (max-width: 480px) {
    flex-direction: column;
    text-align: center;
  }
`;

const Image = styled.img`
  width: 200px;
  height: 200px;
  object-fit: cover;
  
  @media (max-width: 768px) {
    width: 150px;
    height: 150px;
  }
  
  @media (max-width: 480px) {
    width: 100%;
    max-width: 200px;
    height: 150px;
    margin: 0 auto;
  }
`;

const Details = styled.div`
  padding: 10px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  
  @media (max-width: 480px) {
    padding: 15px 0;
    gap: 10px;
  }
`;

const ProductName = styled.span`
  font-size: 16px;
  font-weight: 500;
  
  @media (max-width: 480px) {
    font-size: 14px;
  }
`;

const ProductId = styled.span`
  color: gray;
  font-size: 12px;
`;

const PriceDetail = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 15px;
  
  @media (max-width: 768px) {
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
  }
`;

const ProductAmountContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
`;

const ProductAmount = styled.div`
  font-size: 20px;
  margin: 0 10px;
  
  @media (max-width: 480px) {
    font-size: 18px;
  }
`;

const ProductPrice = styled.div`
  font-size: 24px;
  font-weight: 200;
  
  @media (max-width: 480px) {
    font-size: 20px;
  }
`;

const Hr = styled.hr`
  background-color: #eee;
  border: none;
  height: 1px;
  margin: 20px 0;
`;

const Summary = styled.div`
  flex: 1;
  border: 0.5px solid lightgray;
  border-radius: 10px;
  padding: 20px;
  height: fit-content;
  
  @media (max-width: 768px) {
    margin-top: 20px;
  }
  
  @media (max-width: 480px) {
    padding: 15px;
  }
`;

const SummaryTitle = styled.h1`
  font-weight: 200;
  font-size: 1.5rem;
  
  @media (max-width: 480px) {
    font-size: 1.25rem;
  }
`;

const SummaryItem = styled.div`
  margin: 20px 0px;
  display: flex;
  justify-content: space-between;
  font-weight: ${(props) => props.type === "total" && "500"};
  font-size: ${(props) => props.type === "total" ? "20px" : "16px"};
  
  @media (max-width: 480px) {
    margin: 15px 0px;
    font-size: ${(props) => props.type === "total" ? "18px" : "14px"};
  }
`;

const SummaryItemText = styled.span``;

const SummaryItemPrice = styled.span``;

const Button = styled.button`
  width: 100%;
  padding: 15px;
  background-color: black;
  color: white;
  font-weight: 600;
  border: none;
  cursor: pointer;
  margin-top: 10px;
  border-radius: 8px;
  font-size: 16px;
  min-height: 50px;
  
  &:disabled {
    background-color: #ccc;
    cursor: not-allowed;
  }
  
  @media (max-width: 480px) {
    padding: 12px;
    font-size: 14px;
    min-height: 44px;
  }
`;

const QuantityButton = styled.button`
  width: 35px;
  height: 35px;
  border: 1px solid teal;
  background-color: white;
  cursor: pointer;
  font-size: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 4px;
  min-height: 35px;
  
  @media (max-width: 480px) {
    width: 30px;
    height: 30px;
    font-size: 14px;
  }
`;

const EmptyCart = styled.div`
  text-align: center;
  padding: 50px;
  font-size: 18px;
  color: gray;
  
  @media (max-width: 480px) {
    padding: 30px 20px;
    font-size: 16px;
  }
`;

const FreeShippingBadge = styled.span`
  color: green;
  margin-left: 5px;
  font-size: 12px;
  font-weight: 600;
`;

const AddressSection = styled.div`
  margin: 20px 0;
  padding: 20px;
  border: 1px solid #eee;
  border-radius: 10px;
  background-color: #f9f9f9;
  
  @media (max-width: 480px) {
    padding: 15px;
  }
`;

const AddressSelector = styled.div`
  margin-bottom: 15px;
`;

const AddressOption = styled.div`
  padding: 12px;
  border: 2px solid ${props => props.selected ? '#667eea' : '#e2e8f0'};
  border-radius: 8px;
  margin-bottom: 10px;
  cursor: pointer;
  background: white;
  transition: all 0.3s ease;

  &:hover {
    border-color: #667eea;
  }
`;

const AddressText = styled.p`
  margin: 5px 0;
  color: #2d3748;
  line-height: 1.4;
`;

