// ========== STRIPE PAYMENT FORM COMPONENT ==========
const StripePaymentForm = ({ amount, clientSecret, onSuccess, onError }) => {
  // ... (keep your existing StripePaymentForm component exactly as it is)
};

// ========== MAIN CART PAGE COMPONENT ==========
const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isCheckingOut, setIsCheckingOut] = useState(false);
  const [showAddressSection, setShowAddressSection] = useState(false);
  const [showPayment, setShowPayment] = useState(false);
  const [paymentError, setPaymentError] = useState('');
  const [paymentSuccess, setPaymentSuccess] = useState('');
  const [clientSecret, setClientSecret] = useState('');
  const [currentOrderId, setCurrentOrderId] = useState('');
  const [userAddresses, setUserAddresses] = useState([]);
  const [selectedAddress, setSelectedAddress] = useState(null);
  const [showNewAddressForm, setShowNewAddressForm] = useState(false);
  const navigate = useNavigate();

  const [newAddress, setNewAddress] = useState({
    street: "",
    city: "",
    state: "",
    zipCode: "",
    country: "United States",
    type: "home",
    isDefault: false
  });

  // Helper Functions
  const getAuthHeader = () => {
    const token = localStorage.getItem('token');
    return token ? { Authorization: `Bearer ${token}` } : {};
  };

  const getCurrentUser = () => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const base64Payload = token.split('.')[1];
        const payload = JSON.parse(atob(base64Payload));
        return payload.userId;
      } catch (error) {
        return null;
      }
    }
    return null;
  };

  const calculateShipping = () => {
    const subtotal = getTotal();
    if (subtotal >= 50) {
      return { shipping: 0, hasFreeShipping: true };
    } else {
      return { shipping: 5.90, hasFreeShipping: false };
    }
  };

  const getTotal = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price) * item.quantity, 0);
  };

  const getFinalTotal = () => {
    const shippingInfo = calculateShipping();
    return getTotal() + shippingInfo.shipping;
  };

  // UPDATED: Reliable cart clearing function
  const clearCartReliably = async (userId) => {
    const token = localStorage.getItem('token');
    const BASE_URL = process.env.REACT_APP_API_URL;
    
    console.log('ðŸ”„ Starting reliable cart clearing process...');
    
    // Strategy 1: Try backend API first
    try {
      console.log('ðŸ”„ Attempting backend cart clearing...');
      const response = await axios.delete(
        `${BASE_URL}/api/cart/${userId}/clear`,
        { 
          headers: { Authorization: `Bearer ${token}` },
          timeout: 5000
        }
      );
      
      if (response.data.message === 'Cart cleared successfully') {
        console.log('âœ… Backend cart cleared successfully');
        return true;
      }
    } catch (backendError) {
      console.warn('âš ï¸ Backend cart clearing failed, trying individual item removal:', backendError.message);
      
      // Strategy 2: Remove items one by one
      try {
        for (const item of cartItems) {
          await axios.delete(
            `${BASE_URL}/api/cart/${userId}/${item.productId}`,
            { 
              headers: { Authorization: `Bearer ${token}` },
              timeout: 3000
            }
          );
          console.log(`âœ… Removed item ${item.productId} from cart`);
        }
        console.log('âœ… All items removed individually');
        return true;
      } catch (individualError) {
        console.warn('âš ï¸ Individual item removal failed:', individualError.message);
        
        // Strategy 3: Update quantities to 0
        try {
          for (const item of cartItems) {
            await axios.put(
              `${BASE_URL}/api/cart/${userId}`,
              { productId: item.productId, quantity: 0 },
              { 
                headers: { Authorization: `Bearer ${token}` },
                timeout: 3000
              }
            );
          }
          console.log('âœ… All quantities set to 0');
          return true;
        } catch (quantityError) {
          console.error('âŒ All cart clearing strategies failed:', quantityError.message);
          return false;
        }
      }
    }
    
    return false;
  };

  // Fetch user addresses from profile
  const fetchUserAddresses = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/auth/addresses`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      const addresses = response.data.addresses || [];
      setUserAddresses(addresses);
      
      // Auto-select default address
      const defaultAddress = addresses.find(addr => addr.isDefault);
      if (defaultAddress) {
        setSelectedAddress(defaultAddress);
      } else if (addresses.length > 0) {
        setSelectedAddress(addresses[0]);
      }
    } catch (error) {
      console.error('Error fetching addresses:', error);
    }
  };

  // Add new address to profile
  const addNewAddress = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/auth/addresses`,
        newAddress,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Refresh addresses
      await fetchUserAddresses();
      setShowNewAddressForm(false);
      setNewAddress({
        street: "",
        city: "",
        state: "",
        zipCode: "",
        country: "United States",
        type: "home",
        isDefault: false
      });
    } catch (error) {
      console.error('Error adding address:', error);
      alert('Failed to add address');
    }
  };

  // Function to send payment confirmation email
  const sendPaymentConfirmationEmail = async (orderData, paymentData) => {
    try {
      console.log('ðŸ“§ Sending payment confirmation email for order:', orderData.id);
      
      const user = JSON.parse(localStorage.getItem('user'));
      const userEmail = user?.email;

      if (!userEmail) {
        console.warn('No email found for payment confirmation');
        return false;
      }

      const emailData = {
        paymentData: {
          id: paymentData.id,
          orderId: orderData.id,
          amount: orderData.amount,
          paymentMethod: 'Credit Card',
          status: 'completed',
          paymentDate: new Date().toISOString(),
          receiptUrl: `https://quickcart.com/receipts/${paymentData.id}`,
          products: orderData.products,
          shipping: orderData.shipping || 0,
          tax: orderData.tax || 0
        },
        userEmail: userEmail
      };

      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/email/payment-confirmation`,
        emailData,
        { headers: getAuthHeader() }
      );

      console.log('âœ… Payment confirmation email sent successfully:', response.data);
      return true;
    } catch (emailError) {
      console.error('âŒ Failed to send payment confirmation email:', emailError);
      return false;
    }
  };

  // Function to send order confirmation email
  const sendOrderConfirmationEmail = async (orderData) => {
    try {
      console.log('ðŸ“§ Sending order confirmation email for order:', orderData.id);
      
      const user = JSON.parse(localStorage.getItem('user'));
      const userEmail = user?.email;

      if (!userEmail) {
        console.warn('No email found for order confirmation');
        return false;
      }

      const emailData = {
        orderData: orderData,
        userEmail: userEmail
      };

      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/email/order-confirmation`,
        emailData,
        { headers: getAuthHeader() }
      );

      console.log('âœ… Order confirmation email sent successfully:', response.data);
      return true;
    } catch (emailError) {
      console.error('âŒ Failed to send order confirmation email:', emailError);
      return false;
    }
  };

  // Clear payment flag function
  const clearPaymentFlag = () => {
    localStorage.removeItem('paymentCompleted');
    console.log('ðŸ—‘ï¸ Payment flag cleared');
  };

  const fetchCart = useCallback(async () => {
    const userId = getCurrentUser();
    if (!userId) {
      setCartItems([]);
      return;
    }

    try {
      setLoading(true);
      console.log('ðŸ”„ Fetching cart...');
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        { headers: getAuthHeader() }
      );
      console.log('âœ… Cart data received:', response.data.length, 'items');
      setCartItems(response.data);
    } catch (error) {
      console.error('Error fetching cart:', error);
    } finally {
      setLoading(false);
    }
  }, []);

  const updateCartItem = async (productId, quantity) => {
    const userId = getCurrentUser();
    if (!userId) return;

    try {
      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        { productId, quantity },
        { headers: getAuthHeader() }
      );
      await fetchCart();
    } catch (error) {
      console.error('Error updating cart:', error);
    }
  };

  const removeFromCart = async (productId) => {
    const userId = getCurrentUser();
    if (!userId) return;

    try {
      await axios.delete(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}/${productId}`,
        { headers: getAuthHeader() }
      );
      await fetchCart();
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  };

  // Clear payment flag on component mount and setup debug logging
  useEffect(() => {
    console.log('ðŸ” CartPage mounted, clearing payment flag');
    clearPaymentFlag();
    fetchCart();
    fetchUserAddresses();
  }, [fetchCart]);

  // Debug cart updates
  useEffect(() => {
    console.log('ðŸ“¦ Cart items updated:', {
      itemCount: cartItems.length,
      items: cartItems,
      paymentFlag: localStorage.getItem('paymentCompleted')
    });
  }, [cartItems]);

  const handleQuantity = (productId, quantity) => {
    if (quantity === 0) {
      removeFromCart(productId);
    } else {
      updateCartItem(productId, quantity);
    }
  };

  const handlePayment = async () => {
    setPaymentError('');
    setPaymentSuccess('');
    setIsCheckingOut(true);

    try {
      const userId = getCurrentUser();
      const token = localStorage.getItem('token');
      const BASE_URL = process.env.REACT_APP_API_URL;

      // Validate address first
      if (!selectedAddress) {
        throw new Error('Please select a shipping address');
      }

      // Validate cart items
      if (cartItems.length === 0) {
        throw new Error('Cart is empty');
      }

      console.log('ðŸ”„ Starting payment process...');
      console.log('ðŸ“¦ Cart items:', cartItems);
      console.log('ðŸ  Selected address:', selectedAddress);
      console.log('ðŸ’° Total amount:', getFinalTotal());

      // Step 1: Create order FIRST with proper data structure
      const orderData = {
        cartItems: cartItems.map(item => ({
          productId: item.productId || item.id,
          title: item.title || item.name,
          price: parseFloat(item.price),
          quantity: item.quantity,
          size: item.size || 'One Size',
          color: item.color || 'Default',
          img: item.img || item.image
        })),
        address: selectedAddress,
        totalAmount: getFinalTotal()
      };

      console.log('ðŸ“¦ Creating order with data:', orderData);

      const orderResponse = await axios.post(
        `${BASE_URL}/api/orders`,
        orderData,
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          timeout: 10000
        }
      );

      console.log('âœ… Order creation response:', orderResponse.data);

      if (!orderResponse.data.order || !orderResponse.data.order.id) {
        throw new Error('Order creation failed - no order ID returned');
      }

      const createdOrderId = orderResponse.data.order.id;
      setCurrentOrderId(createdOrderId);

      console.log('âœ… Order created successfully:', createdOrderId);

      // Step 2: Create payment intent with the order ID
      const amount = Math.round(getFinalTotal() * 100);

      console.log('ðŸ’³ Creating payment intent for amount:', amount);

      const paymentResponse = await axios.post(
        `${BASE_URL}/api/payments/create-payment-intent`,
        { 
          orderId: createdOrderId,
          amount: amount,
          currency: 'usd'
        },
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          timeout: 10000
        }
      );

      console.log('âœ… Payment intent response:', paymentResponse.data);

      if (paymentResponse.data.clientSecret) {
        console.log('âœ… Payment intent created, client secret received');
        setClientSecret(paymentResponse.data.clientSecret);
      } else {
        throw new Error('No client secret received from payment service');
      }

    } catch (error) {
      console.error('âŒ Payment setup error:', error);
      const errorMessage = error.response?.data?.error || error.response?.data?.message || error.message;
      setPaymentError(`Payment setup failed: ${errorMessage}`);
      setIsCheckingOut(false);
    }
  };

  // UPDATED: Reliable payment success handler
  const handlePaymentSuccess = async (paymentIntent) => {
    try {
      const userId = getCurrentUser();
      const token = localStorage.getItem('token');
      const BASE_URL = process.env.REACT_APP_API_URL;
      const userEmail = JSON.parse(localStorage.getItem('user'))?.email;

      console.log('âœ… Payment successful, paymentIntent:', paymentIntent);
      
      // STEP 1: CLEAR CART RELIABLY (MOST IMPORTANT)
      console.log('ðŸ›’ Starting reliable cart clearing...');
      const cartCleared = await clearCartReliably(userId);
      
      // STEP 2: Clear cart locally regardless of backend success
      setCartItems([]);
      console.log('âœ… Cart cleared locally');
      
      if (!cartCleared) {
        console.warn('âš ï¸ Backend cart clearing had issues, but continuing with payment success');
      }

      // STEP 3: Try to confirm payment with backend (optional)
      let paymentConfirmed = false;
      try {
        const paymentConfirmation = await axios.post(
          `${BASE_URL}/api/payments/confirm-payment`,
          { 
            paymentIntentId: paymentIntent.id,
            orderId: currentOrderId 
          },
          { 
            headers: { Authorization: `Bearer ${token}` },
            timeout: 10000
          }
        );

        console.log('âœ… Payment confirmed by backend:', paymentConfirmation.data);
        paymentConfirmed = true;
      } catch (confirmError) {
        console.warn('âš ï¸ Payment confirmation API failed, but continuing:', confirmError.message);
      }

      // STEP 4: Verify order exists in database
      let orderVerified = false;
      try {
        const orderVerification = await axios.get(
          `${BASE_URL}/api/orders/${currentOrderId}`,
          { 
            headers: { Authorization: `Bearer ${token}` },
            timeout: 5000
          }
        );

        if (orderVerification.data.order) {
          orderVerified = true;
          console.log('âœ… Order verified in database:', orderVerification.data.order);
        }
      } catch (orderError) {
        console.error('âŒ Order verification failed:', orderError);
      }

      // STEP 5: Update order payment status if order exists
      if (orderVerified) {
        try {
          const orderUpdateResponse = await axios.put(
            `${BASE_URL}/api/orders/${currentOrderId}/payment-status`,
            { 
              paymentStatus: 'paid'
            },
            { 
              headers: { Authorization: `Bearer ${token}` },
              timeout: 5000
            }
          );
          console.log('âœ… Order payment status updated:', orderUpdateResponse.data);
        } catch (orderError) {
          console.error('âš ï¸ Order status update failed:', orderError);
        }
      }

      // STEP 6: Prepare success data
      const orderDataForSuccess = {
        id: currentOrderId,
        amount: getFinalTotal().toFixed(2),
        products: cartItems.map(item => ({
          title: item.title || item.name,
          price: item.price,
          quantity: item.quantity,
          img: item.img || item.image,
          size: item.size,
          color: item.color
        })),
        address: selectedAddress,
        paymentMethod: 'Credit Card',
        status: 'confirmed',
        paymentStatus: 'paid',
        createdAt: new Date().toISOString(),
        paymentIntentId: paymentIntent.id,
        shipping: calculateShipping().shipping,
        tax: 0
      };

      localStorage.setItem('lastOrder', JSON.stringify(orderDataForSuccess));
      if (userEmail) {
        localStorage.setItem('userEmail', userEmail);
      }

      // STEP 7: SEND EMAILS (non-blocking)
      console.log('ðŸ“§ Sending emails...');
      Promise.all([
        sendPaymentConfirmationEmail(orderDataForSuccess, paymentIntent),
        sendOrderConfirmationEmail(orderDataForSuccess)
      ]).then(([paymentEmailSent, orderEmailSent]) => {
        console.log('ðŸ“§ Email status:', {
          paymentEmail: paymentEmailSent ? 'âœ… Sent' : 'âŒ Failed',
          orderEmail: orderEmailSent ? 'âœ… Sent' : 'âŒ Failed'
        });
      }).catch(emailError => {
        console.error('ðŸ“§ Email sending error:', emailError);
      });

      console.log('ðŸŽ‰ Payment completed successfully, redirecting...');

      // STEP 8: Set flag to prevent cart refetching
      localStorage.setItem('paymentCompleted', 'true');
      localStorage.setItem('lastCartClearTime', Date.now().toString());

      // STEP 9: Show success message and redirect
      setPaymentSuccess('Payment successful! Cart cleared. Redirecting...');
      
      setTimeout(() => {
        setShowPayment(false);
        setClientSecret('');
        navigate('/success');
      }, 1500);

    } catch (error) {
      console.error('âŒ Post-payment processing error:', error);
      const errorMessage = error.response?.data?.error || error.message;
      
      // Even if there are issues, if payment was successful with Stripe, proceed
      if (paymentIntent.status === 'succeeded') {
        console.log('âš ï¸ Minor issues but Stripe payment succeeded, proceeding...');
        
        // Clear cart locally anyway as fallback
        setCartItems([]);
        localStorage.setItem('paymentCompleted', 'true');
        localStorage.setItem('lastCartClearTime', Date.now().toString());
        
        setPaymentSuccess('Payment successful! Minor processing issues. Redirecting...');
        
        setTimeout(() => {
          setShowPayment(false);
          setClientSecret('');
          navigate('/success');
        }, 1500);
      } else {
        setPaymentError(`Payment processing failed: ${errorMessage}`);
      }
    }
  };

  const handlePaymentError = (errorMessage) => {
    console.error('âŒ Payment error:', errorMessage);
    setPaymentError(`Payment failed: ${errorMessage}`);
  };

  const handleCheckout = () => {
    if (cartItems.length === 0) {
      alert('Your cart is empty');
      return;
    }
    
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to checkout');
      navigate('/login');
      return;
    }

    if (!showAddressSection) {
      setShowAddressSection(true);
      return;
    }

    // Validate address before proceeding to payment
    if (!selectedAddress) {
      alert('Please select a shipping address');
      return;
    }

    setShowPayment(true);
    handlePayment(); // Start the payment process
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewAddress(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleContinueShopping = () => {
    // Clear payment flag when user continues shopping
    clearPaymentFlag();
    navigate('/');
  };

  const handleManageAddresses = () => {
    navigate('/addresses');
  };

  const shippingInfo = calculateShipping();

  if (loading) {
    return (
      <Container>
        <Wrapper>
          <Title>YOUR BAG</Title>
          <div style={{ textAlign: 'center', padding: '50px' }}>Loading cart...</div>
        </Wrapper>
      </Container>
    );
  }

  return (
    <Container>
      <Wrapper>
        <Title>YOUR BAG</Title>
        <Top>
          <TopButton onClick={handleContinueShopping}>CONTINUE SHOPPING</TopButton>
          <TopTexts>
            <TopText>Shopping Bag({cartItems.length})</TopText>
            <TopText>Your Wishlist (0)</TopText>
          </TopTexts>
          <TopButton 
            type="filled" 
            onClick={handleCheckout}
            disabled={cartItems.length === 0}
          >
            {isCheckingOut ? 'PROCESSING...' : 'CHECKOUT NOW'}
          </TopButton>
        </Top>
        <Bottom>
          <Info>
            {cartItems.length === 0 ? (
              <EmptyCart>
                <h3>Your cart is empty</h3>
                <p>Add some products to your cart to see them here</p>
                <TopButton onClick={handleContinueShopping} style={{ marginTop: '20px' }}>
                  START SHOPPING
                </TopButton>
              </EmptyCart>
            ) : (
              cartItems.map((item) => (
                <div key={item.id}>
                  <Product>
                    <ProductDetail>
                      <Image src={item.img || 'https://via.placeholder.com/200x200?text=No+Image'} />
                      <Details>
                        <ProductName>
                          <b>Product:</b> {item.title}
                        </ProductName>
                        <ProductId>
                          <b>ID:</b> {item.productId}
                        </ProductId>
                        {item.size && (
                          <div>
                            <b>Size:</b> {item.size}
                          </div>
                        )}
                        {item.color && (
                          <div>
                            <b>Color:</b> {item.color}
                          </div>
                        )}
                      </Details>
                    </ProductDetail>
                    <PriceDetail>
                      <ProductAmountContainer>
                        <QuantityButton onClick={() => handleQuantity(item.productId, item.quantity - 1)}>
                          -
                        </QuantityButton>
                        <ProductAmount>{item.quantity}</ProductAmount>
                        <QuantityButton onClick={() => handleQuantity(item.productId, item.quantity + 1)}>
                          +
                        </QuantityButton>
                      </ProductAmountContainer>
                      <ProductPrice>$ {(parseFloat(item.price) * item.quantity).toFixed(2)}</ProductPrice>
                    </PriceDetail>
                  </Product>
                  <Hr />
                </div>
              ))
            )}
          </Info>
          {cartItems.length > 0 && (
            <Summary>
              <SummaryTitle>ORDER SUMMARY</SummaryTitle>
              <SummaryItem>
                <SummaryItemText>Subtotal</SummaryItemText>
                <SummaryItemPrice>$ {getTotal().toFixed(2)}</SummaryItemPrice>
              </SummaryItem>
              
              <SummaryItem>
                <SummaryItemText>
                  Shipping
                  {shippingInfo.hasFreeShipping && (
                    <FreeShippingBadge>ðŸšš FREE</FreeShippingBadge>
                  )}
                </SummaryItemText>
                <SummaryItemPrice>
                  {shippingInfo.hasFreeShipping ? '$ 0.00' : `$ ${shippingInfo.shipping.toFixed(2)}`}
                </SummaryItemPrice>
              </SummaryItem>

              {!shippingInfo.hasFreeShipping && (
                <SummaryItem>
                  <SummaryItemText style={{ fontSize: '12px', color: '#666', fontStyle: 'italic' }}>
                    Add ${(50 - getTotal()).toFixed(2)} more for free shipping!
                  </SummaryItemText>
                  <SummaryItemPrice></SummaryItemPrice>
                </SummaryItem>
              )}

              <SummaryItem type="total">
                <SummaryItemText>Total</SummaryItemText>
                <SummaryItemPrice>$ {getFinalTotal().toFixed(2)}</SummaryItemPrice>
              </SummaryItem>

              {showAddressSection && (
                <AddressSection>
                  <h3>Shipping Address</h3>
                  
                  {userAddresses.length > 0 ? (
                    <AddressSelector>
                      <p>Select a saved address:</p>
                      {userAddresses.map((address) => (
                        <AddressOption 
                          key={address.id}
                          selected={selectedAddress?.id === address.id}
                          onClick={() => setSelectedAddress(address)}
                        >
                          <div>
                            <AddressType isDefault={address.isDefault}>
                              {address.type} {address.isDefault && '(Default)'}
                            </AddressType>
                          </div>
                          <AddressText>{address.street}</AddressText>
                          <AddressText>{address.city}, {address.state} {address.zipCode}</AddressText>
                          <AddressText>{address.country}</AddressText>
                        </AddressOption>
                      ))}
                    </AddressSelector>
                  ) : (
                    <p>No saved addresses found.</p>
                  )}

                  {!showNewAddressForm ? (
                    <div>
                      <NewAddressButton onClick={() => setShowNewAddressForm(true)}>
                        + Add New Address
                      </NewAddressButton>
                      <NewAddressButton onClick={handleManageAddresses} style={{ marginLeft: '10px' }}>
                        ðŸ“‹ Manage Addresses
                      </NewAddressButton>
                    </div>
                  ) : (
                    <div>
                      <h4>Add New Address</h4>
                      <Input
                        type="text"
                        name="street"
                        placeholder="Street Address"
                        value={newAddress.street}
                        onChange={handleInputChange}
                        required
                      />
                      <InputRow>
                        <InputGroup>
                          <Input
                            type="text"
                            name="city"
                            placeholder="City"
                            value={newAddress.city}
                            onChange={handleInputChange}
                            required
                          />
                        </InputGroup>
                        <InputGroup>
                          <Input
                            type="text"
                            name="state"
                            placeholder="State"
                            value={newAddress.state}
                            onChange={handleInputChange}
                            required
                          />
                        </InputGroup>
                      </InputRow>
                      <InputRow>
                        <InputGroup>
                          <Input
                            type="text"
                            name="zipCode"
                            placeholder="ZIP Code"
                            value={newAddress.zipCode}
                            onChange={handleInputChange}
                            required
                          />
                        </InputGroup>
                        <InputGroup>
                          <Input
                            type="text"
                            name="country"
                            placeholder="Country"
                            value={newAddress.country}
                            onChange={handleInputChange}
                            required
                          />
                        </InputGroup>
                      </InputRow>
                      <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                        <NewAddressButton onClick={addNewAddress}>
                          Save Address
                        </NewAddressButton>
                        <NewAddressButton onClick={() => setShowNewAddressForm(false)}>
                          Cancel
                        </NewAddressButton>
                      </div>
                    </div>
                  )}
                </AddressSection>
              )}

              <Button 
                onClick={handleCheckout}
                disabled={cartItems.length === 0 || isCheckingOut || (showAddressSection && !selectedAddress)}
                style={{
                  backgroundColor: cartItems.length === 0 ? '#ccc' : isCheckingOut ? '#ffa500' : '#000'
                }}
              >
                {isCheckingOut ? 'ðŸ”„ PROCESSING...' : 
                 showAddressSection ? 'ðŸ’³ PROCEED TO PAYMENT' : 
                 `ðŸ›’ CHECKOUT NOW ($${getFinalTotal().toFixed(2)})`}
              </Button>
            </Summary>
          )}
        </Bottom>
      </Wrapper>

      {showPayment && (
        <PaymentModal>
          <PaymentContent>
            <h2 style={{ marginBottom: '10px', color: '#333' }}>Complete Your Purchase</h2>
            <p style={{ fontSize: '18px', fontWeight: '600', color: '#2c5aa0', marginBottom: '20px' }}>
              Total Amount: <span style={{ color: '#4CAF50' }}>${getFinalTotal().toFixed(2)}</span>
            </p>
            
            {paymentError && (
              <ErrorMessage>
                <strong>Payment Error:</strong> {paymentError}
                <br />
                <small>Please check your address information and try again.</small>
              </ErrorMessage>
            )}
            {paymentSuccess && <SuccessMessage>{paymentSuccess}</SuccessMessage>}
            
            {!clientSecret && isCheckingOut && (
              <LoadingSpinner>
                <div style={{ marginBottom: '10px' }}>ðŸ”„ Setting up payment...</div>
                <div style={{ fontSize: '14px', color: '#666' }}>
                  Creating order and payment session...
                </div>
              </LoadingSpinner>
            )}
            
            {clientSecret ? (
              <Elements stripe={stripePromise} options={{ clientSecret }}>
                <StripePaymentForm 
                  amount={getFinalTotal().toFixed(2)}
                  clientSecret={clientSecret}
                  onSuccess={handlePaymentSuccess}
                  onError={handlePaymentError}
                />
              </Elements>
            ) : (
              !isCheckingOut && (
                <div>
                  <PayNowBtn 
                    onClick={handlePayment} 
                    disabled={isCheckingOut}
                  >
                    {isCheckingOut ? 'Setting Up Payment...' : 'Try Payment Again'}
                  </PayNowBtn>
                </div>
              )
            )}
            
            <CancelBtn onClick={() => {
              setShowPayment(false);
              setClientSecret('');
              setPaymentError('');
              setPaymentSuccess('');
              setIsCheckingOut(false);
            }}>
              Cancel Payment
            </CancelBtn>
          </PaymentContent>
        </PaymentModal>
      )}
    </Container>
  );
};

export default CartPage;
