import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 60vh;
  text-align: center;
  padding: 20px;
`;

const Title = styled.h1`
  color: green;
  margin-bottom: 20px;
`;

const Message = styled.p`
  font-size: 18px;
  margin-bottom: 30px;
`;

const EmailStatus = styled.div`
  margin: 15px 0;
  padding: 10px 20px;
  border-radius: 8px;
  font-size: 14px;
  
  ${props => props.type === 'loading' && `
    background-color: #e7f3ff;
    color: #0066cc;
    border: 1px solid #b3d9ff;
  `}
  
  ${props => props.type === 'success' && `
    background-color: #d4edda;
    color: #155724;
    border: 1px solid #c3e6cb;
  `}
  
  ${props => props.type === 'error' && `
    background-color: #f8d7da;
    color: #721c24;
    border: 1px solid #f5c6cb;
  `}
`;

const Button = styled.button`
  padding: 15px 30px;
  background-color: black;
  color: white;
  border: none;
  cursor: pointer;
  font-size: 16px;
  margin: 0 10px;
  
  &:hover {
    background-color: #333;
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 15px;
  flex-wrap: wrap;
  justify-content: center;
  
  @media (max-width: 480px) {
    flex-direction: column;
    align-items: center;
  }
`;

const Success = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [emailStatus, setEmailStatus] = useState('sending');

  useEffect(() => {
    const sendConfirmationEmails = async () => {
      try {
        // Get order data from localStorage (assuming you store it during checkout)
        const orderData = JSON.parse(localStorage.getItem('lastOrder'));
        const user = JSON.parse(localStorage.getItem('user'));
        
        if (orderData && user && user.email) {
          // Send order confirmation email
          await axios.post(
            `${process.env.REACT_APP_API_URL}/api/email/order-confirmation`,
            {
              orderData: {
                ...orderData,
                id: orderData.id || `ORD${Date.now()}`,
                createdAt: new Date().toISOString(),
                status: 'confirmed'
              },
              userEmail: user.email
            }
          );

          // Send payment confirmation email
          await axios.post(
            `${process.env.REACT_APP_API_URL}/api/email/payment-confirmation`,
            {
              paymentData: {
                id: `PAY${Date.now()}`,
                orderId: orderData.id || `ORD${Date.now()}`,
                amount: orderData.total || orderData.amount,
                status: 'completed',
                paymentMethod: orderData.paymentMethod || 'Credit Card',
                updatedAt: new Date().toISOString()
              },
              userEmail: user.email
            }
          );

          setEmailStatus('sent');
          
          // Clear the order from localStorage after successful email
          localStorage.removeItem('lastOrder');
        } else {
          setEmailStatus('no-data');
        }
      } catch (error) {
        console.error('Failed to send confirmation emails:', error);
        setEmailStatus('failed');
      }
    };

    sendConfirmationEmails();
  }, []);

  const getEmailStatusMessage = () => {
    switch (emailStatus) {
      case 'sending':
        return 'ðŸ“§ Sending order confirmation...';
      case 'sent':
        return 'âœ… Order and payment confirmation emails sent!';
      case 'failed':
        return 'âš ï¸ Order successful, but email confirmation failed.';
      case 'no-data':
        return 'âœ… Order successful!';
      default:
        return '';
    }
  };

  const getEmailStatusType = () => {
    switch (emailStatus) {
      case 'sending':
        return 'loading';
      case 'sent':
        return 'success';
      case 'failed':
      case 'no-data':
        return 'error';
      default:
        return 'loading';
    }
  };

  return (
    <Container>
      <Title>Order Successful! ðŸŽ‰</Title>
      <Message>Thank you for your purchase. Your order has been placed successfully.</Message>
      
      {emailStatus !== 'no-data' && (
        <EmailStatus type={getEmailStatusType()}>
          {getEmailStatusMessage()}
        </EmailStatus>
      )}
      
      <ButtonContainer>
        <Button onClick={() => navigate('/')}>Continue Shopping</Button>
        <Button onClick={() => navigate('/orders')}>View My Orders</Button>
      </ButtonContainer>
    </Container>
  );
};

export default Success;
