import React, { useState, useEffect, useCallback } from "react";
import styled from "styled-components";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Container = styled.div``;

const Wrapper = styled.div`
  padding: 20px;
`;

const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`;

const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
`;

const TopButton = styled.button`
  padding: 10px;
  font-weight: 600;
  cursor: pointer;
  border: ${(props) => props.type === "filled" && "none"};
  background-color: ${(props) =>
    props.type === "filled" ? "black" : "transparent"};
  color: ${(props) => props.type === "filled" && "white"};
`;

const TopTexts = styled.div``;

const TopText = styled.span`
  text-decoration: underline;
  cursor: pointer;
  margin: 0px 10px;
`;

const Bottom = styled.div`
  display: flex;
  justify-content: space-between;
`;

const Info = styled.div`
  flex: 3;
`;

const Product = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 20px;
`;

const ProductDetail = styled.div`
  flex: 2;
  display: flex;
`;

const Image = styled.img`
  width: 200px;
  height: 200px;
  object-fit: cover;
`;

const Details = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
`;

const ProductName = styled.span`
  font-size: 18px;
  font-weight: 500;
`;

const ProductId = styled.span`
  color: gray;
`;

const PriceDetail = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const ProductAmountContainer = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
`;

const ProductAmount = styled.div`
  font-size: 24px;
  margin: 5px 15px;
`;

const ProductPrice = styled.div`
  font-size: 30px;
  font-weight: 200;
`;

const Hr = styled.hr`
  background-color: #eee;
  border: none;
  height: 1px;
  margin: 20px 0;
`;

const Summary = styled.div`
  flex: 1;
  border: 0.5px solid lightgray;
  border-radius: 10px;
  padding: 20px;
  height: fit-content;
`;

const SummaryTitle = styled.h1`
  font-weight: 200;
`;

const SummaryItem = styled.div`
  margin: 30px 0px;
  display: flex;
  justify-content: space-between;
  font-weight: ${(props) => props.type === "total" && "500"};
  font-size: ${(props) => props.type === "total" && "24px"};
`;

const SummaryItemText = styled.span``;

const SummaryItemPrice = styled.span``;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  background-color: black;
  color: white;
  font-weight: 600;
  border: none;
  cursor: pointer;
  margin-top: 10px;
`;

const QuantityButton = styled.button`
  width: 30px;
  height: 30px;
  border: 1px solid teal;
  background-color: white;
  cursor: pointer;
  font-size: 18px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const EmptyCart = styled.div`
  text-align: center;
  padding: 50px;
  font-size: 18px;
  color: gray;
`;

const FreeShippingBadge = styled.span`
  color: green;
  margin-left: 5px;
  font-size: 12px;
  font-weight: 600;
`;

const AddressForm = styled.div`
  margin: 20px 0;
  padding: 20px;
  border: 1px solid #eee;
  border-radius: 10px;
  background-color: #f9f9f9;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin: 8px 0;
  border: 1px solid #ddd;
  border-radius: 4px;
  font-size: 14px;
`;

const InputRow = styled.div`
  display: flex;
  gap: 10px;
  
  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const InputGroup = styled.div`
  flex: 1;
`;

const PaymentModal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const PaymentContent = styled.div`
  background: white;
  padding: 2rem;
  border-radius: 8px;
  width: 90%;
  max-width: 400px;
  text-align: center;
`;

const PayNowBtn = styled.button`
  background: #4CAF50;
  color: white;
  padding: 12px 24px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  margin: 5px;
`;

const CancelBtn = styled.button`
  background: #f44336;
  color: white;
  padding: 12px 24px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  margin: 5px;
`;

const ErrorMessage = styled.div`
  color: #f44336;
  background-color: #ffebee;
  padding: 10px;
  border-radius: 4px;
  margin: 10px 0;
  font-size: 14px;
`;

const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isCheckingOut, setIsCheckingOut] = useState(false);
  const [showAddressForm, setShowAddressForm] = useState(false);
  const [showPayment, setShowPayment] = useState(false);
  const [paymentError, setPaymentError] = useState('');
  const navigate = useNavigate();

  const [address, setAddress] = useState({
    street: "",
    city: "",
    state: "",
    zipCode: "",
    country: ""
  });

  const getAuthHeader = () => {
    const token = localStorage.getItem('token');
    return token ? { Authorization: `Bearer ${token}` } : {};
  };

  const getCurrentUser = () => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const base64Payload = token.split('.')[1];
        const payload = JSON.parse(atob(base64Payload));
        return payload.userId;
      } catch (error) {
        return null;
      }
    }
    return null;
  };

  // Smart Shipping Calculation
  const calculateShipping = () => {
    const subtotal = getTotal();
    
    // Free shipping for orders over $50
    if (subtotal >= 50) {
      return {
        shipping: 0,
        hasFreeShipping: true
      };
    } else {
      // Add $5.90 shipping for orders under $50
      return {
        shipping: 5.90,
        hasFreeShipping: false
      };
    }
  };

  // Calculate final total including shipping
  const getFinalTotal = () => {
    const shippingInfo = calculateShipping();
    return getTotal() + shippingInfo.shipping;
  };

  const fetchCart = useCallback(async () => {
    const userId = getCurrentUser();
    if (!userId) {
      setCartItems([]);
      return;
    }

    try {
      setLoading(true);
      const response = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        { headers: getAuthHeader() }
      );
      setCartItems(response.data);
    } catch (error) {
      console.error('Error fetching cart:', error);
    } finally {
      setLoading(false);
    }
  }, []);

  const updateCartItem = async (productId, quantity) => {
    const userId = getCurrentUser();
    if (!userId) return;

    try {
      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}`,
        { productId, quantity },
        { headers: getAuthHeader() }
      );
      await fetchCart();
    } catch (error) {
      console.error('Error updating cart:', error);
    }
  };

  const removeFromCart = async (productId) => {
    const userId = getCurrentUser();
    if (!userId) return;

    try {
      await axios.delete(
        `${process.env.REACT_APP_API_URL}/api/cart/${userId}/${productId}`,
        { headers: getAuthHeader() }
      );
      await fetchCart();
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  };

  useEffect(() => {
    fetchCart();
  }, [fetchCart]);

  const handleQuantity = (productId, quantity) => {
    if (quantity === 0) {
      removeFromCart(productId);
    } else {
      updateCartItem(productId, quantity);
    }
  };

  const getTotal = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price) * item.quantity, 0);
  };

  const handlePayment = async () => {
    setPaymentError('');
    setIsCheckingOut(true);

    try {
      const userId = getCurrentUser();
      const token = localStorage.getItem('token');
      
      const BASE_URL = process.env.REACT_APP_API_URL;
      console.log('Using base URL:', BASE_URL);

      // 1. FIRST CREATE THE ORDER
      const orderData = {
        cartItems: cartItems.map(item => ({
          productId: item.productId,
          title: item.title,
          price: item.price,
          quantity: item.quantity,
          size: item.size || 'One Size',
          color: item.color || 'Default',
          img: item.img
        })),
        address: address,
        totalAmount: getFinalTotal()
      };

      console.log('Creating order with data:', orderData);

      // Create order first
      const orderResponse = await axios.post(
        `${BASE_URL}/api/orders`,
        orderData,
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      console.log('Order created:', orderResponse.data);
      const createdOrderId = orderResponse.data.order.id;

      const amount = Math.round(getFinalTotal() * 100); // Convert to cents

      console.log('Creating payment intent for amount:', amount, 'Order ID:', createdOrderId);

      // 2. THEN CREATE PAYMENT INTENT WITH THE ACTUAL ORDER ID
      const paymentResponse = await axios.post(
        `${BASE_URL}/api/payments/create-payment-intent`,
        { 
          orderId: createdOrderId, // Use the actual order ID from orders container
          amount 
        },
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          timeout: 10000
        }
      );

      console.log('Payment intent created:', paymentResponse.data);

      if (paymentResponse.data.clientSecret) {
        // For now, simulate successful payment without actual Stripe confirmation
        
        // Clear cart after successful payment
        try {
          await axios.delete(
            `${BASE_URL}/api/cart/${userId}/clear`,
            { 
              headers: { 
                Authorization: `Bearer ${token}` 
              } 
            }
          );
          console.log('Cart cleared successfully');
        } catch (cartError) {
          console.log('Cart clearing failed, but continuing...', cartError);
        }

        // Clear cart locally
        setCartItems([]);

        // Show success and redirect
        alert(`Order placed successfully! Order ID: ${createdOrderId}`);
        setShowPayment(false);
        navigate('/success');
      }
    } catch (error) {
      console.error('Payment error:', error);
      
      if (error.response) {
        setPaymentError(`Payment failed: ${error.response.data?.error || error.response.statusText}`);
      } else if (error.request) {
        setPaymentError('Payment service is not responding. Please try again later.');
      } else {
        setPaymentError('Payment failed: ' + error.message);
      }
    } finally {
      setIsCheckingOut(false);
    }
  };

  const handleCheckout = () => {
    if (cartItems.length === 0) {
      alert('Your cart is empty');
      return;
    }
    
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to checkout');
      navigate('/login');
      return;
    }

    // Show address form if not shown yet
    if (!showAddressForm) {
      setShowAddressForm(true);
      return;
    }

    // Validate address
    if (!address.street || !address.city || !address.state || !address.zipCode || !address.country) {
      alert('Please fill in all address fields');
      return;
    }

    // Show payment modal
    setShowPayment(true);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setAddress(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleContinueShopping = () => {
    navigate('/');
  };

  // Get shipping info for display
  const shippingInfo = calculateShipping();

  if (loading) {
    return (
      <Container>
        <Wrapper>
          <Title>YOUR BAG</Title>
          <div style={{ textAlign: 'center', padding: '50px' }}>Loading cart...</div>
        </Wrapper>
      </Container>
    );
  }

  return (
    <Container>
      <Wrapper>
        <Title>YOUR BAG</Title>
        <Top>
          <TopButton onClick={handleContinueShopping}>CONTINUE SHOPPING</TopButton>
          <TopTexts>
            <TopText>Shopping Bag({cartItems.length})</TopText>
            <TopText>Your Wishlist (0)</TopText>
          </TopTexts>
          <TopButton 
            type="filled" 
            onClick={handleCheckout}
            disabled={cartItems.length === 0}
          >
            {isCheckingOut ? 'PROCESSING...' : 'CHECKOUT NOW'}
          </TopButton>
        </Top>
        <Bottom>
          <Info>
            {cartItems.length === 0 ? (
              <EmptyCart>
                <h3>Your cart is empty</h3>
                <p>Add some products to your cart to see them here</p>
                <TopButton onClick={handleContinueShopping} style={{ marginTop: '20px' }}>
                  START SHOPPING
                </TopButton>
              </EmptyCart>
            ) : (
              cartItems.map((item) => (
                <div key={item.id}>
                  <Product>
                    <ProductDetail>
                      <Image src={item.img || 'https://via.placeholder.com/200x200?text=No+Image'} />
                      <Details>
                        <ProductName>
                          <b>Product:</b> {item.title}
                        </ProductName>
                        <ProductId>
                          <b>ID:</b> {item.productId}
                        </ProductId>
                        {item.size && (
                          <div>
                            <b>Size:</b> {item.size}
                          </div>
                        )}
                        {item.color && (
                          <div>
                            <b>Color:</b> {item.color}
                          </div>
                        )}
                      </Details>
                    </ProductDetail>
                    <PriceDetail>
                      <ProductAmountContainer>
                        <QuantityButton onClick={() => handleQuantity(item.productId, item.quantity - 1)}>
                          -
                        </QuantityButton>
                        <ProductAmount>{item.quantity}</ProductAmount>
                        <QuantityButton onClick={() => handleQuantity(item.productId, item.quantity + 1)}>
                          +
                        </QuantityButton>
                      </ProductAmountContainer>
                      <ProductPrice>$ {(parseFloat(item.price) * item.quantity).toFixed(2)}</ProductPrice>
                    </PriceDetail>
                  </Product>
                  <Hr />
                </div>
              ))
            )}
          </Info>
          {cartItems.length > 0 && (
            <Summary>
              <SummaryTitle>ORDER SUMMARY</SummaryTitle>
              <SummaryItem>
                <SummaryItemText>Subtotal</SummaryItemText>
                <SummaryItemPrice>$ {getTotal().toFixed(2)}</SummaryItemPrice>
              </SummaryItem>
              
              <SummaryItem>
                <SummaryItemText>
                  Shipping
                  {shippingInfo.hasFreeShipping && (
                    <FreeShippingBadge>ðŸšš FREE</FreeShippingBadge>
                  )}
                </SummaryItemText>
                <SummaryItemPrice>
                  {shippingInfo.hasFreeShipping ? '$ 0.00' : `$ ${shippingInfo.shipping.toFixed(2)}`}
                </SummaryItemPrice>
              </SummaryItem>

              {!shippingInfo.hasFreeShipping && (
                <SummaryItem>
                  <SummaryItemText style={{ fontSize: '12px', color: '#666', fontStyle: 'italic' }}>
                    Add ${(50 - getTotal()).toFixed(2)} more for free shipping!
                  </SummaryItemText>
                  <SummaryItemPrice></SummaryItemPrice>
                </SummaryItem>
              )}

              <SummaryItem type="total">
                <SummaryItemText>Total</SummaryItemText>
                <SummaryItemPrice>$ {getFinalTotal().toFixed(2)}</SummaryItemPrice>
              </SummaryItem>

              {/* Address Form */}
              {showAddressForm && (
                <AddressForm>
                  <h3>Shipping Address</h3>
                  <Input
                    type="text"
                    name="street"
                    placeholder="Street Address"
                    value={address.street}
                    onChange={handleInputChange}
                    required
                  />
                  <InputRow>
                    <InputGroup>
                      <Input
                        type="text"
                        name="city"
                        placeholder="City"
                        value={address.city}
                        onChange={handleInputChange}
                        required
                      />
                    </InputGroup>
                    <InputGroup>
                      <Input
                        type="text"
                        name="state"
                        placeholder="State"
                        value={address.state}
                        onChange={handleInputChange}
                        required
                      />
                    </InputGroup>
                  </InputRow>
                  <InputRow>
                    <InputGroup>
                      <Input
                        type="text"
                        name="zipCode"
                        placeholder="ZIP Code"
                        value={address.zipCode}
                        onChange={handleInputChange}
                        required
                      />
                    </InputGroup>
                    <InputGroup>
                      <Input
                        type="text"
                        name="country"
                        placeholder="Country"
                        value={address.country}
                        onChange={handleInputChange}
                        required
                      />
                    </InputGroup>
                  </InputRow>
                </AddressForm>
              )}

              <Button 
                onClick={handleCheckout}
                disabled={cartItems.length === 0 || isCheckingOut}
              >
                {isCheckingOut ? 'PROCESSING ORDER...' : 
                 showAddressForm ? 'PROCEED TO PAYMENT' : 
                 `CHECKOUT NOW ($${getFinalTotal().toFixed(2)})`}
              </Button>
            </Summary>
          )}
        </Bottom>
      </Wrapper>

      {/* Payment Modal */}
      {showPayment && (
        <PaymentModal>
          <PaymentContent>
            <h2>Complete Your Purchase</h2>
            <p>Total Amount: ${getFinalTotal().toFixed(2)}</p>
            
            {paymentError && <ErrorMessage>{paymentError}</ErrorMessage>}
            
            <div>
              <PayNowBtn 
                onClick={handlePayment} 
                disabled={isCheckingOut}
              >
                {isCheckingOut ? 'Processing Payment...' : 'Pay Now'}
              </PayNowBtn>
              <CancelBtn onClick={() => setShowPayment(false)}>
                Cancel
              </CancelBtn>
            </div>
          </PaymentContent>
        </PaymentModal>
      )}
    </Container>
  );
};

export default CartPage;
