import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Slider from "../components/Slider";
import Announcement from "../components/Announcement";
import Categories from "../components/Categories";
import Products from "../components/Products";
import Newsletter from "../components/Newsletter";
import axios from "axios";

const Container = styled.div`
  width: 100vw;
  overflow-x: hidden;
  
  /* REMOVE EXTRA SPACE */
  padding-bottom: 0 !important;
  margin-bottom: 0 !important;
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: 50px;
  font-size: 18px;
  
  @media (max-width: 768px) {
    padding: 30px 20px;
    font-size: 16px;
  }
`;

const ErrorMessage = styled.div`
  text-align: center;
  padding: 50px;
  font-size: 18px;
  color: red;
  
  @media (max-width: 768px) {
    padding: 30px 20px;
    font-size: 16px;
  }
`;

const DebugInfo = styled.div`
  background: #f8f9fa;
  padding: 20px;
  margin: 20px;
  border-radius: 8px;
  border-left: 4px solid #007bff;
  
  @media (max-width: 768px) {
    margin: 15px 10px;
    padding: 15px;
    font-size: 14px;
  }
`;

const Home = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [debugInfo, setDebugInfo] = useState("");

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        setError("");
        
        const apiUrl = `${process.env.REACT_APP_API_URL}/api/products`;
        setDebugInfo(`Trying to fetch from: ${apiUrl}`);
        console.log('API URL:', apiUrl);
        
        const response = await axios.get(apiUrl, {
          timeout: 10000 // 10 second timeout
        });
        
        console.log('Full API Response:', response);
        console.log('Products Data:', response.data);
        
        let productsData = [];
        
        // Handle different response formats
        if (response.data && Array.isArray(response.data)) {
          productsData = response.data;
        } else if (response.data && response.data.products && Array.isArray(response.data.products)) {
          productsData = response.data.products;
        } else if (response.data && typeof response.data === 'object') {
          // If it's an object, convert to array
          productsData = Object.values(response.data);
        }
        
        setDebugInfo(`Success! Received ${productsData.length} products`);
        console.log(`Total products received: ${productsData.length}`);
        console.log('All product IDs:', productsData.map(p => p.id));
        console.log('All product names:', productsData.map(p => p.title || p.name));
        
        // Show ALL products without any limits
        setProducts(productsData);
        
      } catch (error) {
        console.error("Error fetching products:", error);
        const errorMessage = error.response 
          ? `Server error: ${error.response.status} - ${error.response.statusText}`
          : error.message || "Network error - Cannot connect to product service";
        
        setError(errorMessage);
        setDebugInfo(`Error: ${errorMessage}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  // Test the API directly in browser
  const testApi = () => {
    const apiUrl = `${process.env.REACT_APP_API_URL}/api/products`;
    window.open(apiUrl, '_blank');
  };

  if (loading) {
    return (
      <Container>
        <Announcement />
        <Slider />
        <Categories />
        <LoadingMessage>Loading products...</LoadingMessage>
        <Newsletter />
      </Container>
    );
  }

  return (
    <Container>
      <Announcement />
      <Slider />
      <Categories />
      
      {error && (
        <>
          <ErrorMessage>
            <h3>Failed to load products</h3>
            <p>{error}</p>
            <button 
              onClick={testApi}
              style={{
                padding: '10px 20px',
                background: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
                marginTop: '10px'
              }}
            >
              Test API Directly
            </button>
          </ErrorMessage>
          <DebugInfo>
            <h4>Debug Information:</h4>
            <p>{debugInfo}</p>
            <p>API URL: {process.env.REACT_APP_API_URL}/api/products</p>
            <p>Check browser console for detailed logs</p>
          </DebugInfo>
        </>
      )}
      
      {/* REMOVED THE "ðŸ“¦ Showing ALL X Products" TEXT - This was causing the extra space */}
      {!error && products.length > 0 && (
        <Products products={products} loading={loading} />
      )}
      
      {!error && products.length === 0 && (
        <ErrorMessage>
          <h3>No Products Found</h3>
          <p>No products available in the database.</p>
          <button onClick={testApi}>Check API Response</button>
        </ErrorMessage>
      )}
      
      <Newsletter />
    </Container>
  );
};

export default Home;
